      'Ïπ¥Ìéò': '‚òï'
    };
    return icons[category] || 'üçΩÔ∏è';
  };

  const getCategoryColor = (category) => {
    const colors = {
      'ÌïúÏãù': '#FF6B6B',
      'ÏñëÏãù': '#4ECDC4',
      'ÏùºÏãù': '#45B7D1',
      'Ï§ëÏãù': '#96CEB4',
      'ÎèôÎÇ®ÏïÑÏãù': '#FFEAA7',
      'Ïπ¥Ìéò': '#DDA0DD'
    };
    return colors[category] || '#667eea';
  };

  // ÏÇ¨Ïö©Ïûê ÌèâÏ†ê Ï≤òÎ¶¨
  const handleRating = async (restaurantId, rating) => {
    if (!currentUser) {
