import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useFavorites } from '../context/FavoriteContext';
import { restaurantAPI } from '../services/api';

import './RestaurantList.css';

function RestaurantList() {
  const [restaurants, setRestaurants] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [userRatings, setUserRatings] = useState({});
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showVisitConfirmModal, setShowVisitConfirmModal] = useState(false);
  const [pendingRating, setPendingRating] = useState(null);
  const { currentUser } = useAuth();
  const { addToFavorites, removeFromFavorites, isInFavorites } = useFavorites();

  const categories = ['all', 'ÌïúÏãù', 'Ï§ëÏãù', 'ÏùºÏãù', 'ÏñëÏãù', 'Î∂ÑÏãù', 'ÌÉúÍµ≠', 'Ïà†', 'Ïπ¥Ìéò', 'ÎîîÏ†ÄÌä∏'];

  // ÏÇ¨Ïö©Ïûê ÌèâÏ†ê Î∂àÎü¨Ïò§Í∏∞ (Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄ ÏÇ¨Ïö©)
  const loadUserRatings = async () => {
    if (!currentUser) return;

    try {
      // Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄÏóêÏÑú ÏÇ¨Ïö©Ïûê ÌèâÏ†ê Î∂àÎü¨Ïò§Í∏∞
      const savedRatings = localStorage.getItem('userRatings');
      if (savedRatings) {
        setUserRatings(JSON.parse(savedRatings));
      }
    } catch (error) {
      console.error('ÌèâÏ†ê Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:', error);
    }
  };

  // ÏÇ¨Ïö©Ïûê Î°úÍ∑∏Ïù∏ Ïãú ÌèâÏ†ê Î∂àÎü¨Ïò§Í∏∞
  useEffect(() => {
    if (currentUser) {
      loadUserRatings();
    } else {
      setUserRatings({});
    }
  }, [currentUser]);

  useEffect(() => {
    const fetchRestaurants = async () => {
      try {
        setLoading(true);
        const { data } = await restaurantAPI.getAll();

        const restaurantsWithExtraData = data.map(restaurant => ({
          ...restaurant,
          category: restaurant.cuisine, // cuisineÏùÑ categoryÎ°ú Îß§Ìïë
          location: { lat: restaurant.latitude, lng: restaurant.longitude }, // positionÏùÑ locationÏúºÎ°ú Îß§Ìïë
          totalRatings: 0, // ÏûÑÏãú Îç∞Ïù¥ÌÑ∞
          price: "2ÎßåÏõê~5ÎßåÏõê", // ÏûÑÏãú Îç∞Ïù¥ÌÑ∞
          hours: "11:00 - 22:00", // ÏûÑÏãú Îç∞Ïù¥ÌÑ∞
          userRatings: {}
        }));

        // Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄÏóêÏÑú ÏÇ¨Ïö©Ïûê ÌèâÏ†ê Î∂àÎü¨Ïò§Í∏∞
        const savedRatings = localStorage.getItem('userRatings');
        if (savedRatings) {
          setUserRatings(JSON.parse(savedRatings));
        }

        setRestaurants(restaurantsWithExtraData);
      } catch (err) {
        console.error('Failed to fetch restaurants:', err);
        setError('Î†àÏä§ÌÜ†Îûë Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      } finally {
        setLoading(false);
      }
    };

    fetchRestaurants();
  }, []);

  // Î∞©Î¨∏ ÌôïÏù∏ Î™®Îã¨ Ïó¥Í∏∞
  const openVisitConfirmModal = (restaurantId, rating) => {
    setPendingRating({ restaurantId, rating });
    setShowVisitConfirmModal(true);
  };

  // Î∞©Î¨∏ ÌôïÏù∏ Ï≤òÎ¶¨
  const handleVisitConfirm = async (hasVisited) => {
    setShowVisitConfirmModal(false);

    if (!hasVisited) {
      alert('Î∞©Î¨∏ÌïòÏßÄ ÏïäÏùÄ ÎßõÏßëÏóêÎäî Î≥ÑÏ†êÏùÑ Îß§Í∏∏ Ïàò ÏóÜÏäµÎãàÎã§.\n\nÎ®ºÏ†Ä ÎßõÏßëÏùÑ Î∞©Î¨∏Ìï¥Î≥¥ÏÑ∏Ïöî! üçΩÔ∏è');
      return;
    }

    // Î∞©Î¨∏ÌñàÎã§Î©¥ ÌèâÏ†ê Ï†ÄÏû• ÏßÑÌñâ
    await handleRatingSubmit(pendingRating.restaurantId, pendingRating.rating);
    setPendingRating(null);
  };

  // ÏÇ¨Ïö©Ïûê ÌèâÏ†ê Ï≤òÎ¶¨ (Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄ ÏÇ¨Ïö©)
  const handleRatingSubmit = async (restaurantId, rating) => {
    if (!currentUser) {
      alert('ÌèâÏ†êÏùÑ ÎÇ®Í∏∞Î†§Î©¥ Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.');
      return;
    }

    try {
      // Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄÏóê Î∞©Î¨∏ Í∏∞Î°ù Ï†ÄÏû•
      const visits = JSON.parse(localStorage.getItem('visits') || '[]');
      visits.push({
        userId: currentUser.id,
        restaurantId: restaurantId,
        visitDate: new Date().toISOString(),
        rating: rating,
        comment: `${rating}Ï†ê ÌèâÍ∞Ä`
      });
      localStorage.setItem('visits', JSON.stringify(visits));

      // Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄÏóê ÌèâÏ†ê Ï†ÄÏû•
      const newUserRatings = {
        ...userRatings,
        [restaurantId]: {
          ...userRatings[restaurantId],
          [currentUser.id]: rating
        }
      };

      setUserRatings(newUserRatings);
      localStorage.setItem('userRatings', JSON.stringify(newUserRatings));
      alert('ÌèâÏ†êÍ≥º Î∞©Î¨∏ Í∏∞Î°ùÏù¥ Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§! ‚≠ê');
    } catch (error) {
      console.error('ÌèâÏ†ê Ï†ÄÏû• Ï§ë Ïò§Î•ò:', error);
      alert('ÌèâÏ†ê Ï†ÄÏû• Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    }
  };

  // Î≥ÑÏ†ê ÌÅ¥Î¶≠ Ï≤òÎ¶¨
  const handleRating = (restaurantId, rating) => {
    openVisitConfirmModal(restaurantId, rating);
  };

  // Í≤ÄÏÉâÏñ¥ÏôÄ Ïπ¥ÌÖåÍ≥†Î¶¨Î°ú ÌïÑÌÑ∞ÎßÅ
  const filteredRestaurants = restaurants.filter(restaurant => {
    const matchesCategory = selectedCategory === 'all' || restaurant.category === selectedCategory;
    const matchesSearch = searchTerm === '' || 
      restaurant.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      restaurant.category.toLowerCase().includes(searchTerm.toLowerCase()) ||
      restaurant.address.toLowerCase().includes(searchTerm.toLowerCase()) ||
      restaurant.description.toLowerCase().includes(searchTerm.toLowerCase());

    return matchesCategory && matchesSearch;
  });

  const getUserRating = (restaurantId) => {
    if (!currentUser || !userRatings[restaurantId]) return 0;
    return userRatings[restaurantId][currentUser.id] || 0;
  };

  const getCategoryIcon = (category) => {
    const icons = {
      'ÌïúÏãù': 'üçö',
      'ÏñëÏãù': 'üçù',
      'ÏùºÏãù': 'üç£',
      'Ï§ëÏãù': 'ü•¢',
      'Ïπ¥Ìéò': '‚òï',
      'ÎîîÏ†ÄÌä∏': 'üç∞',
      'Î∂ÑÏãù': 'üç°',
      'Ïà†': 'üç∫',
      'ÌÉúÍµ≠': 'üçú'
    };
    return icons[category] || 'üçΩÔ∏è';
  };

  return (
    <div className="restaurant-list-container">
      {/* Í≤ÄÏÉâÏ∞Ω */}
      <div className="search-section">
        <div className="search-container">
          <input
            type="text"
            placeholder="üîç ÏãùÎãπÎ™Ö, Ïπ¥ÌÖåÍ≥†Î¶¨, Ï£ºÏÜåÎ°ú Í≤ÄÏÉâ..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          {searchTerm && (
            <button 
              className="clear-search-btn"
              onClick={() => setSearchTerm('')}
            >
              ‚úï
            </button>
          )}
        </div>
        {searchTerm && (
          <div className="search-results-info">
            <p>"{searchTerm}" Í≤ÄÏÉâ Í≤∞Í≥º: {filteredRestaurants.length}Í∞ú</p>
          </div>
        )}
      </div>

      <div className="category-nav">
        <div className="category-container">
          {categories.map(category => (
            <button
              key={category}
              className={`category-btn ${selectedCategory === category ? 'active' : ''}`}
              onClick={() => setSelectedCategory(category)}
            >
              {category === 'all' ? 'Ï†ÑÏ≤¥' : category}
            </button>
          ))}
        </div>
      </div>

      {loading ? (
        <div className="loading">Î°úÎî© Ï§ë...</div>
      ) : error ? (
        <div className="error">{error}</div>
      ) : (
        <div className="restaurant-grid">
          {filteredRestaurants.map(restaurant => (
          <div key={restaurant.id} className="restaurant-card">
            <div className="restaurant-info">
              <div className="restaurant-header">
                <h3>{restaurant.name}</h3>
                <div className="category-badge">{restaurant.category}</div>
              </div>

              <div className="restaurant-details">
                <div className="rating-section">
                  <div className="stars">
                    {[1, 2, 3, 4, 5].map(star => (
                      <span key={star} className={star <= restaurant.rating ? 'star filled' : 'star'}>
                        ‚òÖ
                      </span>
                    ))}
                  </div>
                  <span className="rating-text">{restaurant.rating}</span>
                  <span className="total-ratings">({restaurant.totalRatings}Î™Ö Î∞©Î¨∏)</span>
                </div>

                <div className="restaurant-meta">
                  <p className="address">üìç {restaurant.address}</p>

                  <p className="hours">üïí {restaurant.hours}</p>
                  <p className="phone">üìû {restaurant.phone}</p>
                  <p className="parking">üöó {restaurant.parking}</p>
                </div>

                <p className="description">{restaurant.description}</p>
              </div>

              <div className="restaurant-actions">
                <button
                  className={`favorite-btn ${isInFavorites(restaurant.id) ? 'favorited' : ''}`}
                  onClick={() => {
                    if (isInFavorites(restaurant.id)) {
                      removeFromFavorites(restaurant.id);
                    } else {
                      addToFavorites(restaurant);
                    }
                  }}
                >
                  {isInFavorites(restaurant.id) ? '‚ù§Ô∏è Ï∞úÎê®' : 'ü§ç ÎÇ¥ Î™©Î°ùÏóê Îã¥Í∏∞'}
                </button>

                <button
                  className="detail-btn"
                  onClick={() => setSelectedRestaurant(restaurant)}
                >
                  üìã ÏÉÅÏÑ∏ Ï†ïÎ≥¥
                </button>
              </div>


            </div>
          </div>
        ))}
      </div>
      )}

      {/* ÏÉÅÏÑ∏ Ï†ïÎ≥¥ Î™®Îã¨ */}
      {selectedRestaurant && (
        <div className="modal-overlay" onClick={() => setSelectedRestaurant(null)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h2>{selectedRestaurant.name}</h2>
              <button className="close-btn" onClick={() => setSelectedRestaurant(null)}>‚úï</button>
            </div>

            <div className="modal-body">
              <div className="restaurant-info-detail">
                <div className="info-row">
                  <span className="info-label">Ïπ¥ÌÖåÍ≥†Î¶¨:</span>
                  <span className="info-value">{getCategoryIcon(selectedRestaurant.category)} {selectedRestaurant.category}</span>
                </div>

                <div className="info-row">
                  <span className="info-label">ÌèâÏ†ê:</span>
                  <span className="info-value">
                    {[1, 2, 3, 4, 5].map(star => (
                      <span key={star} className={star <= selectedRestaurant.rating ? 'star filled' : 'star'}>
                        ‚òÖ
                      </span>
                    ))}
                    {selectedRestaurant.rating} ({selectedRestaurant.totalRatings}Î™Ö Î∞©Î¨∏)
                  </span>
                </div>

                <div className="info-row">
                  <span className="info-label">Ï£ºÏÜå:</span>
                  <span className="info-value">{selectedRestaurant.address}</span>
                </div>

                <div className="info-row">
                  <span className="info-label">Í∞ÄÍ≤©ÎåÄ:</span>
                  <span className="info-value">{selectedRestaurant.price}</span>
                </div>

                <div className="info-row">
                  <span className="info-label">ÏòÅÏóÖÏãúÍ∞Ñ:</span>
                  <span className="info-value">{selectedRestaurant.hours}</span>
                </div>

                <div className="info-row">
                  <span className="info-label">Ï†ÑÌôîÎ≤àÌò∏:</span>
                  <span className="info-value">{selectedRestaurant.phone}</span>
                </div>

                <div className="info-row">
                  <span className="info-label">Ï£ºÏ∞®:</span>
                  <span className="info-value">{selectedRestaurant.parking}</span>
                </div>

                <div className="info-row">
                  <span className="info-label">ÏÑ§Î™Ö:</span>
                  <span className="info-value">{selectedRestaurant.description}</span>
                </div>
              </div>

              <div className="rating-section-modal">
                <h3>ÌèâÏ†ê ÎÇ®Í∏∞Í∏∞</h3>
                <div className="rating-buttons">
                  {[1, 2, 3, 4, 5].map(rating => (
                    <button
                      key={rating}
                      className={`rating-btn ${getUserRating(selectedRestaurant.id) >= rating ? 'selected' : ''}`}
                      onClick={() => handleRating(selectedRestaurant.id, rating)}
                    >
                      ‚òÖ
                    </button>
                  ))}
                </div>
                <p className="rating-text-modal">
                  ÌòÑÏû¨ ÌèâÏ†ê: {getUserRating(selectedRestaurant.id)}Ï†ê
                </p>
              </div>


            </div>
          </div>
        </div>
      )}

      {/* Î∞©Î¨∏ ÌôïÏù∏ Î™®Îã¨ */}
      {showVisitConfirmModal && (
        <div className="visit-confirm-modal">
          <div className="visit-confirm-modal-content">
            <div className="visit-confirm-modal-header">
              <h3>Î∞©Î¨∏ ÌôïÏù∏</h3>
            </div>
            <div className="visit-confirm-modal-body">
              <p>Ïù¥ ÎßõÏßëÏùÑ Î∞©Î¨∏ÌïòÏÖ®ÎÇòÏöî?</p>
              <p className="visit-note">Î∞©Î¨∏ÌïòÏßÄ ÏïäÏïòÎã§Î©¥ Î≥ÑÏ†êÏùÑ Îß§Í∏∏ Ïàò ÏóÜÏäµÎãàÎã§.</p>
            </div>
            <div className="visit-confirm-modal-actions">
              <button 
                className="visit-confirm-btn yes"
                onClick={() => handleVisitConfirm(true)}
              >
                ÎÑ§
              </button>
              <button 
                className="visit-confirm-btn no"
                onClick={() => handleVisitConfirm(false)}
              >
                ÏïÑÎãàÏò§
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default RestaurantList;
