import React, { useState, useEffect } from 'react';
import { useFavorites } from '../context/FavoriteContext';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../services/supabase';
import './Favorites.css';

function Favorites() {
  const { favorites, removeFromFavorites } = useFavorites();
  const { currentUser } = useAuth();
  const navigate = useNavigate();

  console.log('Favorites Ïª¥Ìè¨ÎÑåÌä∏ Î†åÎçîÎßÅ:', { 
    favoritesCount: favorites.length, 
    currentUser: currentUser?.id,
    currentUserEmail: currentUser?.email,
    favorites: favorites.map(f => ({ id: f.id, name: f.name, cuisine: f.cuisine }))
  });
  const [userRatings, setUserRatings] = useState({});
  const [showRatingModal, setShowRatingModal] = useState(false);
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [showVisitConfirmModal, setShowVisitConfirmModal] = useState(false);
  const [pendingRating, setPendingRating] = useState(null);

  // ÏÇ¨Ïö©Ïûê ÌèâÏ†ê Î∂àÎü¨Ïò§Í∏∞ (Supabase ÏßÅÏ†ë Ïó∞Í≤∞)
  const loadUserRatings = async () => {
    if (!currentUser) return;

    try {
      // SupabaseÏóêÏÑú ÏÇ¨Ïö©Ïûê Î¶¨Î∑∞ Í∞ÄÏ†∏Ïò§Í∏∞
      const { data: reviews, error } = await supabase
        .from('reviews')
        .select('*')
        .eq('user_id', currentUser.id);

      if (error) {
        console.error('ÌèâÏ†ê Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:', error);
        return;
      }

      const ratingsMap = {};
      reviews.forEach(review => {
        ratingsMap[review.restaurant_id] = {
          ...ratingsMap[review.restaurant_id],
          [currentUser.id]: review.rating
        };
      });
      setUserRatings(ratingsMap);
      console.log('SupabaseÏóêÏÑú ÌèâÏ†ê Î°úÎìú ÏôÑÎ£å:', reviews.length, 'Í∞ú');
    } catch (error) {
      console.error('ÌèâÏ†ê Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:', error);
    }
  };

  // ÏÇ¨Ïö©Ïûê Î°úÍ∑∏Ïù∏ Ïãú ÌèâÏ†ê Î∂àÎü¨Ïò§Í∏∞
  useEffect(() => {
    if (currentUser) {
      loadUserRatings();
    } else {
      setUserRatings({});
    }
  }, [currentUser]);

  const getCategoryIcon = (category) => {
    if (!category) return 'üçΩÔ∏è';
    const icons = {
      'ÌïúÏãù': 'üçö',
      'ÏñëÏãù': 'üçù',
      'ÏùºÏãù': 'üç£',
      'Ï§ëÏãù': 'ü•¢',
      'ÎèôÎÇ®ÏïÑÏãù': 'üçú',
      'Ïπ¥Ìéò': '‚òï',
      'Í∏∞ÌÉÄ': 'üçΩÔ∏è'
    };
    return icons[category] || 'üçΩÔ∏è';
  };

  const getCategoryColor = (category) => {
    if (!category) return '#667eea';
    const colors = {
      'ÌïúÏãù': '#FF6B6B',
      'ÏñëÏãù': '#4ECDC4',
      'ÏùºÏãù': '#45B7D1',
      'Ï§ëÏãù': '#96CEB4',
      'ÎèôÎÇ®ÏïÑÏãù': '#FFEAA7',
      'Ïπ¥Ìéò': '#DDA0DD',
      'Í∏∞ÌÉÄ': '#667eea'
    };
    return colors[category] || '#667eea';
  };

  // Î∞©Î¨∏ ÌôïÏù∏ Î™®Îã¨ Ïó¥Í∏∞
  const openVisitConfirmModal = (restaurantId, rating) => {
    setPendingRating({ restaurantId, rating });
    setShowVisitConfirmModal(true);
  };

  // Î∞©Î¨∏ ÌôïÏù∏ Ï≤òÎ¶¨
  const handleVisitConfirm = async (hasVisited) => {
    setShowVisitConfirmModal(false);

    if (!hasVisited) {
      alert('Î∞©Î¨∏ÌïòÏßÄ ÏïäÏùÄ ÎßõÏßëÏóêÎäî Î≥ÑÏ†êÏùÑ Îß§Í∏∏ Ïàò ÏóÜÏäµÎãàÎã§.\n\nÎ®ºÏ†Ä ÎßõÏßëÏùÑ Î∞©Î¨∏Ìï¥Î≥¥ÏÑ∏Ïöî! üçΩÔ∏è');
      return;
    }

    // Î∞©Î¨∏ÌñàÎã§Î©¥ ÌèâÏ†ê Ï†ÄÏû• ÏßÑÌñâ
    await handleRatingSubmit(pendingRating.restaurantId, pendingRating.rating);
    setPendingRating(null);
  };

  // ÏÇ¨Ïö©Ïûê ÌèâÏ†ê Ï≤òÎ¶¨ (Supabase ÏßÅÏ†ë Ïó∞Í≤∞)
  const handleRatingSubmit = async (restaurantId, rating) => {
    if (!currentUser) {
      alert('ÌèâÏ†êÏùÑ ÎÇ®Í∏∞Î†§Î©¥ Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.');
      return;
    }

    console.log('ÌèâÏ†ê Ï†ÄÏû• ÏãúÎèÑ:', { currentUser, restaurantId, rating });

    try {
      // Í∏∞Ï°¥ Î¶¨Î∑∞Í∞Ä ÏûàÎäîÏßÄ ÌôïÏù∏
      const { data: existingReview, error: checkError } = await supabase
        .from('reviews')
        .select('*')
        .eq('user_id', currentUser.id)
        .eq('restaurant_id', restaurantId)
        .single();

      if (checkError && checkError.code !== 'PGRST116') {
        console.error('Í∏∞Ï°¥ Î¶¨Î∑∞ ÌôïÏù∏ Ïã§Ìå®:', checkError);
      }

      // Í∏∞Ï°¥ Î¶¨Î∑∞Í∞Ä ÏûàÏúºÎ©¥ ÏóÖÎç∞Ïù¥Ìä∏, ÏóÜÏúºÎ©¥ ÏÉàÎ°ú ÏÉùÏÑ±
      let reviewResult;
      if (existingReview) {
        // Í∏∞Ï°¥ Î¶¨Î∑∞ ÏóÖÎç∞Ïù¥Ìä∏
        const { error: updateError } = await supabase
          .from('reviews')
          .update({
            rating: rating,
            comment: `${rating}Ï†ê ÌèâÍ∞Ä`,
            updated_at: new Date().toISOString()
          })
          .eq('user_id', currentUser.id)
          .eq('restaurant_id', restaurantId);

        if (updateError) {
          console.error('Î¶¨Î∑∞ ÏóÖÎç∞Ïù¥Ìä∏ Ïã§Ìå®:', updateError);
          alert('ÌèâÏ†ê ÏóÖÎç∞Ïù¥Ìä∏Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
          return;
        }
        console.log('Í∏∞Ï°¥ Î¶¨Î∑∞ ÏóÖÎç∞Ïù¥Ìä∏ ÏôÑÎ£å');
      } else {
        // ÏÉà Î¶¨Î∑∞ ÏÉùÏÑ±
        const { error: insertError } = await supabase
          .from('reviews')
          .insert({
            user_id: currentUser.id,
            restaurant_id: restaurantId,
            rating: rating,
            comment: `${rating}Ï†ê ÌèâÍ∞Ä`
          });

        if (insertError) {
          console.error('Î¶¨Î∑∞ Ï†ÄÏû• Ïã§Ìå®:', insertError);
          alert('ÌèâÏ†ê Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
          return;
        }
        console.log('ÏÉà Î¶¨Î∑∞ Ï†ÄÏû• ÏôÑÎ£å');
      }

      // Î∞©Î¨∏ Í∏∞Î°ùÎèÑ Ìï®Íªò Ï†ÄÏû• (Ï§ëÎ≥µ Î∞©ÏßÄ)
      const { data: existingVisit, error: visitCheckError } = await supabase
        .from('visits')
        .select('*')
        .eq('user_id', currentUser.id)
        .eq('restaurant_id', restaurantId)
        .single();

      if (visitCheckError && visitCheckError.code !== 'PGRST116') {
        console.error('Í∏∞Ï°¥ Î∞©Î¨∏ Í∏∞Î°ù ÌôïÏù∏ Ïã§Ìå®:', visitCheckError);
      }

      if (!existingVisit) {
        // Î∞©Î¨∏ Í∏∞Î°ùÏù¥ ÏóÜÏúºÎ©¥ ÏÉàÎ°ú ÏÉùÏÑ±
        const { error: visitError } = await supabase
          .from('visits')
          .insert({
            user_id: currentUser.id,
            restaurant_id: restaurantId,
            rating: rating,
            review: `${rating}Ï†ê ÌèâÍ∞Ä`
          });

        if (visitError) {
          console.error('Î∞©Î¨∏ Í∏∞Î°ù Ï†ÄÏû• Ïã§Ìå®:', visitError);
        } else {
          console.log('Î∞©Î¨∏ Í∏∞Î°ù Ï†ÄÏû• ÏôÑÎ£å');
        }
      }

      // ÏÑ±Í≥µ Ïãú ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
      const newUserRatings = {
        ...userRatings,
        [restaurantId]: {
          ...userRatings[restaurantId],
          [currentUser.id]: rating
        }
      };

      setUserRatings(newUserRatings);
      localStorage.setItem('userRatings', JSON.stringify(newUserRatings));
      setShowRatingModal(false);
      setSelectedRestaurant(null);
      alert('ÌèâÏ†êÏù¥ Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§! ‚≠ê');
      console.log('SupabaseÏóê ÌèâÏ†ê Ï†ÄÏû• ÏôÑÎ£å');
    } catch (error) {
      console.error('ÌèâÏ†ê Ï†ÄÏû• Ï§ë Ïò§Î•ò:', error);
      alert('ÌèâÏ†ê Ï†ÄÏû• Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    }
  };

  // Î≥ÑÏ†ê ÌÅ¥Î¶≠ Ï≤òÎ¶¨
  const handleRating = (restaurantId, rating) => {
    openVisitConfirmModal(restaurantId, rating);
  };

  // ÏÇ¨Ïö©Ïûê ÌèâÏ†ê Í∞ÄÏ†∏Ïò§Í∏∞
  const getUserRating = (restaurantId) => {
    if (!currentUser || !userRatings[restaurantId]) return 0;
    return userRatings[restaurantId][currentUser.id] || 0;
  };

  // ÌèâÍ∞Ä Î™®Îã¨ Ïó¥Í∏∞
  const openRatingModal = (restaurant) => {
    setSelectedRestaurant(restaurant);
    setShowRatingModal(true);
  };

  // ÏßÄÎèÑ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô (ÌäπÏ†ï ÏãùÎãπ ÏúÑÏπòÎ°ú)
  const goToMap = (restaurant) => {
    // ÏãùÎãπÏóê location Ï†ïÎ≥¥Í∞Ä ÏûàÎäîÏßÄ ÌôïÏù∏ (ÎßõÏßë Î™©Î°ùÏóêÏÑúÎäî location ÏÇ¨Ïö©)
    if (restaurant.location && restaurant.location.lat && restaurant.location.lng) {
      // ÏãùÎãπ Ï†ïÎ≥¥Î•º URL ÌååÎùºÎØ∏ÌÑ∞Î°ú Ï†ÑÎã¨
      navigate(`/map?restaurant=${restaurant.id}&lat=${restaurant.location.lat}&lng=${restaurant.location.lng}`);
    } else if (restaurant.position && restaurant.position.lat && restaurant.position.lng) {
      // position Ï†ïÎ≥¥Í∞Ä ÏûàÎäî Í≤ΩÏö∞ (ÏßÄÎèÑ ÌéòÏù¥ÏßÄÏóêÏÑú ÏÇ¨Ïö©)
      navigate(`/map?restaurant=${restaurant.id}&lat=${restaurant.position.lat}&lng=${restaurant.position.lng}`);
    } else {
      // ÏúÑÏπò Ï†ïÎ≥¥Í∞Ä ÏóÜÏúºÎ©¥ Í∏∞Î≥∏ ÏßÄÎèÑ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
      navigate('/map');
    }
  };

  if (favorites.length === 0) {
    return (
      <div className="favorites-container">
        <div className="favorites-header">
          <h2>üíñ ÎÇ¥ Ï∞ú Î™©Î°ù</h2>
          <p>ÎßàÏùåÏóê ÎìúÎäî ÎßõÏßëÏùÑ Ï∞úÌï¥Î≥¥ÏÑ∏Ïöî!</p>
        </div>
        <div className="empty-favorites">
          <div className="empty-icon">üíî</div>
          <h3>ÏïÑÏßÅ Ï∞úÌïú ÎßõÏßëÏù¥ ÏóÜÏñ¥Ïöî</h3>
          <p>ÎßõÏßë Î™©Î°ùÏóêÏÑú "ÎÇ¥ Î™©Î°ùÏóê Îã¥Í∏∞" Î≤ÑÌäºÏùÑ ÎàåÎü¨ÏÑú<br />ÎßàÏùåÏóê ÎìúÎäî ÎßõÏßëÏùÑ Ï∞úÌï¥Î≥¥ÏÑ∏Ïöî!</p>
        </div>
      </div>
    );
  }

  return (
    <div className="favorites-container">
      <div className="favorites-header">
        <h2>üíñ ÎÇ¥ Ï∞ú Î™©Î°ù</h2>
        <p>Ï¥ù {favorites.length}Í∞úÏùò ÎßõÏßëÏùÑ Ï∞úÌïòÏÖ®ÎÑ§Ïöî!</p>
      </div>

      <div className="favorites-grid">
        {favorites.map((restaurant) => (
          <div key={restaurant.id} className="favorite-card" onClick={(e) => e.stopPropagation()}>
            <button 
              className="remove-btn"
              onClick={(e) => {
                e.preventDefault();
                e.stopPropagation();
                console.log('X Î≤ÑÌäº ÌÅ¥Î¶≠Îê®:', restaurant.id, restaurant.name);

                // ÏÇ¨Ïö©Ïûê ÌôïÏù∏
                if (window.confirm(`${restaurant.name}ÏùÑ Ï∞ú Î™©Î°ùÏóêÏÑú Ï†úÍ±∞ÌïòÏãúÍ≤†ÏäµÎãàÍπå?`)) {
                  removeFromFavorites(restaurant.id);
                }
              }}
              onMouseDown={(e) => e.stopPropagation()}
              title="Ï∞ú Î™©Î°ùÏóêÏÑú Ï†úÍ±∞"
              aria-label={`${restaurant.name}ÏùÑ Ï∞ú Î™©Î°ùÏóêÏÑú Ï†úÍ±∞`}
            >
              ‚ùå
            </button>
                          <div className="card-header">
                <div className="restaurant-info">
                  <h3 className="restaurant-name">{restaurant.name}</h3>
                  <div className="category-badge" style={{ backgroundColor: getCategoryColor(restaurant.cuisine || restaurant.category) }}>
                    {getCategoryIcon(restaurant.cuisine || restaurant.category)} {restaurant.cuisine || restaurant.category || 'Í∏∞ÌÉÄ'}
                  </div>
                </div>
              </div>

            <div className="card-body">
              <div className="rating">
                <span className="stars">‚òÖ‚òÖ‚òÖ‚òÖ‚òÖ</span>
                <span className="rating-number">{restaurant.rating}</span>
              </div>

              <p className="description">{restaurant.description}</p>

              <div className="details">
                <p className="address">üìç {restaurant.address}</p>
                <p className="price">üí∞ {restaurant.price}</p>
              </div>
            </div>

            <div className="card-actions">
              <button 
                className="view-map-btn"
                onClick={() => goToMap(restaurant)}
              >
                üó∫Ô∏è ÏßÄÎèÑÏóêÏÑú Î≥¥Í∏∞
              </button>
              <button 
                className="rating-btn"
                onClick={() => openRatingModal(restaurant)}
              >
                ‚≠ê ÌèâÍ∞ÄÌïòÍ∏∞
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* ÌèâÍ∞Ä Î™®Îã¨ */}
      {showRatingModal && selectedRestaurant && (
        <div className="rating-modal">
          <div className="rating-modal-content">
            <div className="rating-modal-header">
              <h3>{selectedRestaurant.name} ÌèâÍ∞ÄÌïòÍ∏∞</h3>
              <button 
                className="close-btn"
                onClick={() => {
                  setShowRatingModal(false);
                  setSelectedRestaurant(null);
                }}
              >
                ‚úï
              </button>
            </div>
            <div className="rating-modal-body">
              <div className="restaurant-info-modal">
                <div className="category-badge" style={{ backgroundColor: getCategoryColor(selectedRestaurant.category) }}>
                  {getCategoryIcon(selectedRestaurant.category)} {selectedRestaurant.category}
                </div>
                <p className="description">{selectedRestaurant.description}</p>
                <div className="details">
                  <p className="address">üìç {selectedRestaurant.address}</p>
                  <p className="price">üí∞ {selectedRestaurant.price}</p>
                </div>
              </div>

              <div className="rating-section">
                <h4>Î≥ÑÏ†ê ÌèâÍ∞Ä</h4>
                <div className="star-rating">
                  {[1, 2, 3, 4, 5].map(star => (
                    <button
                      key={star}
                      className={`star-btn ${getUserRating(selectedRestaurant.id) >= star ? 'filled' : ''}`}
                      onClick={() => handleRating(selectedRestaurant.id, star)}
                    >
                      ‚òÖ
                    </button>
                  ))}
                </div>
                <p className="rating-text">
                  ÌòÑÏû¨ ÌèâÏ†ê: {getUserRating(selectedRestaurant.id)}Ï†ê
                </p>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Î∞©Î¨∏ ÌôïÏù∏ Î™®Îã¨ */}
      {showVisitConfirmModal && (
        <div className="visit-confirm-modal">
          <div className="visit-confirm-modal-content">
            <div className="visit-confirm-modal-header">
              <h3>Î∞©Î¨∏ ÌôïÏù∏</h3>
            </div>
