import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import LocationPermission from '../components/LocationPermission';
import { getLocationPermissionStatus, getLocationWithCache } from '../utils/locationUtils';
import './LocationGuide.css';

const LocationGuide = () => {
  const [permissionStatus, setPermissionStatus] = useState('unknown');
  const [userLocation, setUserLocation] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    checkPermissionStatus();
  }, []);

  const checkPermissionStatus = async () => {
    try {
      const status = await getLocationPermissionStatus();
      setPermissionStatus(status);

      if (status === 'granted') {
        // 이미 권한이 허용된 경우 위치 정보 가져오기
        try {
          const location = await getLocationWithCache();
          setUserLocation(location);
        } catch (error) {
          console.warn('위치 정보 가져오기 실패:', error);
        }
      }
    } catch (error) {
      console.error('권한 상태 확인 실패:', error);
      setPermissionStatus('unknown');
    } finally {
      setIsLoading(false);
    }
  };

  const handleLocationGranted = (position) => {
    setPermissionStatus('granted');
    if (position) {
      setUserLocation({
        lat: position.coords.latitude,
        lng: position.coords.longitude
      });
    }
  };

  const handleLocationDenied = (error) => {
    setPermissionStatus('denied');
    console.error('위치 정보 거부됨:', error);
  };

  const handleContinue = () => {
    // 위치 정보가 있으면 지도 페이지로, 없으면 홈으로
    if (userLocation) {
      navigate('/map');
    } else {
      navigate('/');
    }
  };

  const handleSkip = () => {
    navigate('/');
  };

  if (isLoading) {
    return (
      <div className="location-guide-container">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>위치 정보를 확인하고 있습니다...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="location-guide-container">
      <div className="location-guide-content">
        <div className="guide-header">
          <h1>📍 위치 정보 설정</h1>
          <p>더 나은 맛집 추천을 위해 위치 정보를 설정해주세요</p>
        </div>

        <div className="guide-benefits">
          <h2>위치 정보를 사용하면</h2>
          <div className="benefits-grid">
            <div className="benefit-card">
              <div className="benefit-icon">🎯</div>
              <h3>정확한 추천</h3>
              <p>내 위치 기반으로 가까운 맛집을 정확하게 추천해드립니다</p>
            </div>
            <div className="benefit-card">
              <div className="benefit-icon">🗺️</div>
              <h3>지도 서비스</h3>
              <p>내 위치를 중심으로 한 지도에서 맛집을 쉽게 찾을 수 있습니다</p>
            </div>
            <div className="benefit-card">
              <div className="benefit-icon">📏</div>
              <h3>거리 필터</h3>
              <p>원하는 거리 내의 맛집만 필터링해서 볼 수 있습니다</p>
            </div>
            <div className="benefit-card">
              <div className="benefit-icon">🚶‍♂️</div>
              <h3>길찾기</h3>
              <p>선택한 맛집까지의 최적 경로를 안내받을 수 있습니다</p>
            </div>
          </div>
        </div>

        <div className="permission-section">
          <LocationPermission 
            onLocationGranted={handleLocationGranted}
            onLocationDenied={handleLocationDenied}
          />
        </div>

        {permissionStatus === 'granted' && userLocation && (
          <div className="location-success">
            <div className="success-card">
              <div className="success-icon">✅</div>
              <h3>위치 정보 설정 완료!</h3>
              <p>현재 위치: {userLocation.lat.toFixed(6)}, {userLocation.lng.toFixed(6)}</p>
              <div className="success-actions">
                <button className="continue-btn" onClick={handleContinue}>
                  지도에서 맛집 보기
                </button>
                <button className="skip-btn" onClick={handleSkip}>
                  홈으로 이동
                </button>
              </div>
            </div>
          </div>
        )}

        <div className="guide-footer">
          <button className="skip-location-btn" onClick={handleSkip}>
            위치 정보 없이 시작하기
          </button>
          <p className="privacy-note">
            위치 정보는 맛집 추천 목적으로만 사용되며,<br />
            다른 용도로 사용되지 않습니다.
          </p>
        </div>
      </div>
    </div>
  );
};

export default LocationGuide;
