import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import LocationPermission from '../components/LocationPermission';
import { getLocationPermissionStatus, getLocationWithCache } from '../utils/locationUtils';
import './LocationGuide.css';

const LocationGuide = () => {
  const [permissionStatus, setPermissionStatus] = useState('unknown');
  const [userLocation, setUserLocation] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    checkPermissionStatus();
  }, []);

  const checkPermissionStatus = async () => {
    try {
      const status = await getLocationPermissionStatus();
      setPermissionStatus(status);

      if (status === 'granted') {
        // Ïù¥ÎØ∏ Í∂åÌïúÏù¥ ÌóàÏö©Îêú Í≤ΩÏö∞ ÏúÑÏπò Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
        try {
          const location = await getLocationWithCache();
          setUserLocation(location);
        } catch (error) {
          console.warn('ÏúÑÏπò Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞ Ïã§Ìå®:', error);
        }
      }
    } catch (error) {
      console.error('Í∂åÌïú ÏÉÅÌÉú ÌôïÏù∏ Ïã§Ìå®:', error);
      setPermissionStatus('unknown');
    } finally {
      setIsLoading(false);
    }
  };

  const handleLocationGranted = (position) => {
    setPermissionStatus('granted');
    if (position) {
      setUserLocation({
        lat: position.coords.latitude,
        lng: position.coords.longitude
      });
    }
  };

  const handleLocationDenied = (error) => {
    setPermissionStatus('denied');
    console.error('ÏúÑÏπò Ï†ïÎ≥¥ Í±∞Î∂ÄÎê®:', error);
  };

  const handleContinue = () => {
    // ÏúÑÏπò Ï†ïÎ≥¥Í∞Ä ÏûàÏúºÎ©¥ ÏßÄÎèÑ ÌéòÏù¥ÏßÄÎ°ú, ÏóÜÏúºÎ©¥ ÌôàÏúºÎ°ú
    if (userLocation) {
      navigate('/map');
    } else {
      navigate('/');
    }
  };

  const handleSkip = () => {
    navigate('/');
  };

  if (isLoading) {
    return (
      <div className="location-guide-container">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>ÏúÑÏπò Ï†ïÎ≥¥Î•º ÌôïÏù∏ÌïòÍ≥† ÏûàÏäµÎãàÎã§...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="location-guide-container">
      <div className="location-guide-content">
        <div className="guide-header">
          <h1>üìç ÏúÑÏπò Ï†ïÎ≥¥ ÏÑ§Ï†ï</h1>
          <p>Îçî ÎÇòÏùÄ ÎßõÏßë Ï∂îÏ≤úÏùÑ ÏúÑÌï¥ ÏúÑÏπò Ï†ïÎ≥¥Î•º ÏÑ§Ï†ïÌï¥Ï£ºÏÑ∏Ïöî</p>
        </div>

        <div className="guide-benefits">
          <h2>ÏúÑÏπò Ï†ïÎ≥¥Î•º ÏÇ¨Ïö©ÌïòÎ©¥</h2>
          <div className="benefits-grid">
            <div className="benefit-card">
              <div className="benefit-icon">üéØ</div>
              <h3>Ï†ïÌôïÌïú Ï∂îÏ≤ú</h3>
              <p>ÎÇ¥ ÏúÑÏπò Í∏∞Î∞òÏúºÎ°ú Í∞ÄÍπåÏö¥ ÎßõÏßëÏùÑ Ï†ïÌôïÌïòÍ≤å Ï∂îÏ≤úÌï¥ÎìúÎ¶ΩÎãàÎã§</p>
            </div>
            <div className="benefit-card">
              <div className="benefit-icon">üó∫Ô∏è</div>
              <h3>ÏßÄÎèÑ ÏÑúÎπÑÏä§</h3>
              <p>ÎÇ¥ ÏúÑÏπòÎ•º Ï§ëÏã¨ÏúºÎ°ú Ìïú ÏßÄÎèÑÏóêÏÑú ÎßõÏßëÏùÑ ÏâΩÍ≤å Ï∞æÏùÑ Ïàò ÏûàÏäµÎãàÎã§</p>
            </div>
            <div className="benefit-card">
              <div className="benefit-icon">üìè</div>
              <h3>Í±∞Î¶¨ ÌïÑÌÑ∞</h3>
              <p>ÏõêÌïòÎäî Í±∞Î¶¨ ÎÇ¥Ïùò ÎßõÏßëÎßå ÌïÑÌÑ∞ÎßÅÌï¥ÏÑú Î≥º Ïàò ÏûàÏäµÎãàÎã§</p>
            </div>
            <div className="benefit-card">
              <div className="benefit-icon">üö∂‚Äç‚ôÇÔ∏è</div>
              <h3>Í∏∏Ï∞æÍ∏∞</h3>
              <p>ÏÑ†ÌÉùÌïú ÎßõÏßëÍπåÏßÄÏùò ÏµúÏ†Å Í≤ΩÎ°úÎ•º ÏïàÎÇ¥Î∞õÏùÑ Ïàò ÏûàÏäµÎãàÎã§</p>
            </div>
          </div>
        </div>

        <div className="permission-section">
          <LocationPermission 
            onLocationGranted={handleLocationGranted}
            onLocationDenied={handleLocationDenied}
          />
        </div>

        {permissionStatus === 'granted' && userLocation && (
          <div className="location-success">
            <div className="success-card">
              <div className="success-icon">‚úÖ</div>
              <h3>ÏúÑÏπò Ï†ïÎ≥¥ ÏÑ§Ï†ï ÏôÑÎ£å!</h3>
              <p>ÌòÑÏû¨ ÏúÑÏπò: {userLocation.lat.toFixed(6)}, {userLocation.lng.toFixed(6)}</p>
              <div className="success-actions">
                <button className="continue-btn" onClick={handleContinue}>
                  ÏßÄÎèÑÏóêÏÑú ÎßõÏßë Î≥¥Í∏∞
                </button>
                <button className="skip-btn" onClick={handleSkip}>
                  ÌôàÏúºÎ°ú Ïù¥Îèô
                </button>
              </div>
            </div>
          </div>
        )}

        <div className="guide-footer">
          <button className="skip-location-btn" onClick={handleSkip}>
            ÏúÑÏπò Ï†ïÎ≥¥ ÏóÜÏù¥ ÏãúÏûëÌïòÍ∏∞
          </button>
          <p className="privacy-note">
            ÏúÑÏπò Ï†ïÎ≥¥Îäî ÎßõÏßë Ï∂îÏ≤ú Î™©Ï†ÅÏúºÎ°úÎßå ÏÇ¨Ïö©ÎêòÎ©∞,<br />
            Îã§Î•∏ Ïö©ÎèÑÎ°ú ÏÇ¨Ïö©ÎêòÏßÄ ÏïäÏäµÎãàÎã§.
          </p>
        </div>
      </div>
    </div>
  );
};

export default LocationGuide;
