import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useFavorites } from '../context/FavoriteContext';
import './RestaurantDetailModal.css';

const RestaurantDetailModal = ({ restaurant, onClose, onRating }) => {
  const { currentUser } = useAuth();
  const { addToFavorites, removeFromFavorites, isInFavorites } = useFavorites();
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [modalPosition, setModalPosition] = useState({ x: 0, y: 0 });

  // ÎìúÎûòÍ∑∏ Ïù¥Î≤§Ìä∏ Ìï∏Îì§Îü¨
  const handleMouseDown = (e) => {
    setIsDragging(true);
    const rect = e.currentTarget.getBoundingClientRect();
    setDragOffset({
      x: e.clientX - rect.left,
      y: e.clientY - rect.top
    });
  };

  const handleMouseMove = (e) => {
    if (isDragging) {
      setModalPosition({
        x: e.clientX - dragOffset.x,
        y: e.clientY - dragOffset.y
      });
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const getCategoryIcon = (category) => {
    const icons = {
      'ÌïúÏãù': 'üçö',
      'ÏñëÏãù': 'üçù',
      'ÏùºÏãù': 'üç£',
      'Ï§ëÏãù': 'ü•¢',
      'ÎèôÎÇ®ÏïÑÏãù': 'üçú',
      'Ïπ¥Ìéò': '‚òï'
    };
    return icons[category] || 'üçΩÔ∏è';
  };

  const renderStars = (rating) => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <span key={i} className={`star ${i <= rating ? 'filled' : ''}`}>
          ‚òÖ
        </span>
      );
    }
    return stars;
  };

  if (!restaurant) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div 
        className="modal-content" 
        onClick={(e) => e.stopPropagation()}
        style={{
          transform: `translate(${modalPosition.x}px, ${modalPosition.y}px)`
        }}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
      >
        <div className="modal-header">
          <h2>{restaurant.name}</h2>
          <button className="close-btn" onClick={onClose}>‚úï</button>
        </div>

        <div className="modal-body">
          <div className="restaurant-info-detail">
            <div className="info-row">
              <span className="info-label">Ïπ¥ÌÖåÍ≥†Î¶¨:</span>
              <span className="info-value">{getCategoryIcon(restaurant.category)} {restaurant.category}</span>
            </div>

            <div className="info-row">
              <span className="info-label">ÌèâÏ†ê:</span>
              <span className="info-value">
                {renderStars(restaurant.rating)}
                {restaurant.rating} ({restaurant.totalRatings || 0}Í∞ú Î¶¨Î∑∞)
              </span>
            </div>

            <div className="info-row">
              <span className="info-label">Ï£ºÏÜå:</span>
              <span className="info-value">{restaurant.address}</span>
            </div>

            <div className="info-row">
              <span className="info-label">Í∞ÄÍ≤©ÎåÄ:</span>
              <span className="info-value">{restaurant.price}</span>
            </div>

            <div className="info-row">
              <span className="info-label">ÏòÅÏóÖÏãúÍ∞Ñ:</span>
              <span className="info-value">{restaurant.businessHours}</span>
            </div>

            <div className="info-row">
              <span className="info-label">Ï†ÑÌôîÎ≤àÌò∏:</span>
              <span className="info-value">{restaurant.phone}</span>
            </div>

            <div className="info-row">
              <span className="info-label">Ï£ºÏ∞®:</span>
              <span className="info-value">{restaurant.parking}</span>
            </div>

            <div className="info-row">
              <span className="info-label">ÏÑ§Î™Ö:</span>
              <span className="info-value">{restaurant.description}</span>
            </div>
          </div>

          <div className="modal-actions">
            <button
              className={`favorite-btn ${isInFavorites(restaurant.id) ? 'favorited' : ''}`}
              onClick={() => {
                if (isInFavorites(restaurant.id)) {
                  removeFromFavorites(restaurant.id);
                  alert('Ï∞ú Î™©Î°ùÏóêÏÑú Ï†úÍ±∞ÎêòÏóàÏäµÎãàÎã§! üëã');
                } else {
                  addToFavorites(restaurant);
                  alert('Ï∞ú Î™©Î°ùÏóê Ï∂îÍ∞ÄÎêòÏóàÏäµÎãàÎã§! üéâ');
                }
                onClose();
              }}
            >
              {isInFavorites(restaurant.id) ? '‚ù§Ô∏è Ï∞úÎê®' : 'ü§ç Ï∞úÌïòÍ∏∞'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RestaurantDetailModal;
