import React, { useState, useEffect } from 'react';
import './LocationPermission.css';

const LocationPermission = ({ onLocationGranted, onLocationDenied }) => {
  const [permissionStatus, setPermissionStatus] = useState('unknown');
  const [isRequesting, setIsRequesting] = useState(false);

  useEffect(() => {
    // 브라우저가 위치 정보를 지원하는지 확인
    if (!navigator.geolocation) {
      setPermissionStatus('not-supported');
      return;
    }

    // 현재 권한 상태 확인
    if (navigator.permissions) {
      navigator.permissions.query({ name: 'geolocation' }).then((result) => {
        setPermissionStatus(result.state);

        // 권한 상태 변경 감지
        result.onchange = () => {
          setPermissionStatus(result.state);
          if (result.state === 'granted') {
            onLocationGranted && onLocationGranted();
          } else if (result.state === 'denied') {
            onLocationDenied && onLocationDenied();
          }
        };
      });
    }
  }, [onLocationGranted, onLocationDenied]);

  const requestLocationPermission = () => {
    setIsRequesting(true);

    navigator.geolocation.getCurrentPosition(
      (position) => {
        setPermissionStatus('granted');
        setIsRequesting(false);
        onLocationGranted && onLocationGranted(position);
      },
      (error) => {
        setPermissionStatus('denied');
        setIsRequesting(false);
        onLocationDenied && onLocationDenied(error);
      },
      {
        enableHighAccuracy: true,
        timeout: 10000,
        maximumAge: 300000 // 5분
      }
    );
  };

  const openLocationSettings = () => {
    // 브라우저별 위치 설정 페이지 안내
    const userAgent = navigator.userAgent;
    let settingsUrl = '';

    if (userAgent.includes('Chrome')) {
      settingsUrl = 'chrome://settings/content/location';
    } else if (userAgent.includes('Firefox')) {
      settingsUrl = 'about:preferences#privacy';
    } else if (userAgent.includes('Safari')) {
      settingsUrl = 'https://support.apple.com/ko-kr/guide/safari/sfri40732/mac';
    }

    if (settingsUrl) {
      window.open(settingsUrl, '_blank');
    } else {
      alert('브라우저 설정에서 위치 정보 접근을 허용해주세요.');
    }
  };

  if (permissionStatus === 'not-supported') {
    return (
      <div className="location-permission-container">
        <div className="location-permission-card">
          <div className="location-icon">⚠️</div>
          <h3>위치 정보를 지원하지 않습니다</h3>
          <p>이 브라우저는 위치 정보 기능을 지원하지 않습니다.<br />
          최신 브라우저로 업데이트해주세요.</p>
        </div>
      </div>
    );
  }

  if (permissionStatus === 'granted') {
    return (
      <div className="location-permission-container">
        <div className="location-permission-card success">
          <div className="location-icon">✅</div>
          <h3>위치 정보 접근이 허용되었습니다</h3>
          <p>주변 맛집 추천과 지도 기능을 사용할 수 있습니다.</p>
        </div>
      </div>
    );
  }

  if (permissionStatus === 'denied') {
    return (
      <div className="location-permission-container">
        <div className="location-permission-card error">
          <div className="location-icon">❌</div>
          <h3>위치 정보 접근이 거부되었습니다</h3>
          <p>주변 맛집 추천을 위해 위치 정보 접근을 허용해주세요.</p>
          <div className="location-actions">
            <button 
              className="location-settings-btn"
              onClick={openLocationSettings}
            >
              브라우저 설정 열기
            </button>
            <button 
              className="location-retry-btn"
              onClick={requestLocationPermission}
            >
              다시 시도
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="location-permission-container">
      <div className="location-permission-card">
        <div className="location-icon">📍</div>
        <h3>위치 정보 접근 권한이 필요합니다</h3>
        <p>주변 맛집 추천과 지도 기능을 사용하기 위해<br />
        위치 정보 접근을 허용해주세요.</p>
        <div className="location-benefits">
          <div className="benefit-item">
            <span className="benefit-icon">🎯</span>
            <span>정확한 주변 맛집 추천</span>
          </div>
          <div className="benefit-item">
            <span className="benefit-icon">🗺️</span>
            <span>내 위치 기반 지도 서비스</span>
          </div>
          <div className="benefit-item">
            <span className="benefit-icon">📏</span>
            <span>거리 기반 필터링</span>
          </div>
        </div>
        <button 
          className={`location-request-btn ${isRequesting ? 'loading' : ''}`}
          onClick={requestLocationPermission}
          disabled={isRequesting}
        >
          {isRequesting ? '위치 확인 중...' : '위치 정보 허용하기'}
        </button>
        <p className="location-note">
          위치 정보는 맛집 추천 목적으로만 사용되며,<br />
          다른 용도로 사용되지 않습니다.
        </p>
      </div>
    </div>
  );
};

export default LocationPermission;
