import React, { useState, useEffect } from 'react';
import { useFavorites } from '../context/FavoriteContext';

const LocationBasedRecommendation = () => {
  const [userLocation, setUserLocation] = useState(null);
  const [nearbyRestaurants, setNearbyRestaurants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const { addToFavorites, removeFromFavorites, isInFavorites } = useFavorites();

  // Îëê ÏßÄÏ†ê Í∞ÑÏùò Í±∞Î¶¨ Í≥ÑÏÇ∞ (ÌïòÎ≤ÑÏÇ¨Ïù∏ Í≥µÏãù)
  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // ÏßÄÍµ¨Ïùò Î∞òÏßÄÎ¶Ñ (km)
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = 
      Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * 
      Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    const distance = R * c; // km
    return distance;
  };

  // Î∞±ÏóîÎìúÏóêÏÑú Î†àÏä§ÌÜ†Îûë Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
  const fetchRestaurants = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/restaurants');
      if (!response.ok) {
        throw new Error('Î†àÏä§ÌÜ†Îûë Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }
      const data = await response.json();

      // Î∞©Î¨∏ Ïàò Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
      const visitCountsResponse = await fetch('http://localhost:8080/api/visits/count/all');
      let visitCounts = {};
      if (visitCountsResponse.ok) {
        visitCounts = await visitCountsResponse.json();
      }

      // Î∞©Î¨∏ Ïàò Îç∞Ïù¥ÌÑ∞Î•º Î†àÏä§ÌÜ†Îûë Îç∞Ïù¥ÌÑ∞Ïóê Ï∂îÍ∞Ä
      const restaurantsWithVisitCounts = data.map(restaurant => ({
        ...restaurant,
        totalRatings: visitCounts[restaurant.id] || 0
      }));

      return restaurantsWithVisitCounts;
    } catch (error) {
      console.error('Failed to fetch restaurants:', error);
      throw error;
    }
  };

  // ÏÇ¨Ïö©Ïûê ÏúÑÏπò Í∞ÄÏ†∏Ïò§Í∏∞
  const getUserLocation = async () => {
    setLoading(true);
    setError(null);

    if (!navigator.geolocation) {
      setError('Ïù¥ Î∏åÎùºÏö∞Ï†ÄÏóêÏÑúÎäî ÏúÑÏπò Ï†ïÎ≥¥Î•º ÏßÄÏõêÌïòÏßÄ ÏïäÏäµÎãàÎã§.');
      setLoading(false);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        try {
          const { latitude, longitude } = position.coords;
          setUserLocation({ lat: latitude, lng: longitude });

          // Î∞±ÏóîÎìúÏóêÏÑú Î†àÏä§ÌÜ†Îûë Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
          const restaurants = await fetchRestaurants();

          // Í∞ÄÍπåÏö¥ ÏãùÎãπ Ï∞æÍ∏∞ (ÏúÑÎèÑ/Í≤ΩÎèÑÍ∞Ä ÏûàÎäî Í≤ΩÏö∞Îßå)
          const restaurantsWithDistance = restaurants
            .filter(restaurant => restaurant.latitude && restaurant.longitude)
            .map(restaurant => {
              const distance = calculateDistance(
                latitude, 
                longitude, 
                restaurant.latitude, 
                restaurant.longitude
              );
              return {
                ...restaurant,
                distance: distance,
                position: { lat: restaurant.latitude, lng: restaurant.longitude },
                businessHours: "11:00 - 22:00", // ÏûÑÏãú Îç∞Ïù¥ÌÑ∞
              };
            });

          // ÏúÑÎèÑ/Í≤ΩÎèÑÍ∞Ä ÏóÜÎäî Í≤ΩÏö∞ Í∏∞Î≥∏ ÏúÑÏπò Îç∞Ïù¥ÌÑ∞ Ï∂îÍ∞Ä
          const restaurantsWithoutLocation = restaurants
            .filter(restaurant => !restaurant.latitude || !restaurant.longitude)
            .map(restaurant => ({
              ...restaurant,
              distance: Math.random() * 10 + 1, // ÏûÑÏãú Í±∞Î¶¨ (1-11km)
              position: restaurant.position || { lat: 37.5665, lng: 126.9780 }, // Í∏∞Ï°¥ position ÏÇ¨Ïö© ÎòêÎäî Í∏∞Î≥∏Í∞í
              businessHours: "11:00 - 22:00",
            }));

          const allRestaurants = [...restaurantsWithDistance, ...restaurantsWithoutLocation];

          // Í±∞Î¶¨ÏàúÏúºÎ°ú Ï†ïÎ†¨ (Í∞ÄÍπåÏö¥ Ïàú) - 3Í∞úÎßå Ï∂îÏ≤ú
          const sortedRestaurants = allRestaurants
            .sort((a, b) => a.distance - b.distance)
            .slice(0, 3); // ÏÉÅÏúÑ 3Í∞úÎßå Ï∂îÏ≤ú

          setNearbyRestaurants(sortedRestaurants);
          setLoading(false);
        } catch (error) {
          setError('Î†àÏä§ÌÜ†Îûë Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
          setLoading(false);
        }
      },
      (error) => {
        let errorMessage = 'ÏúÑÏπò Ï†ïÎ≥¥Î•º Í∞ÄÏ†∏Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§.';
        switch(error.code) {
          case error.PERMISSION_DENIED:
            errorMessage = 'ÏúÑÏπò Ï†ïÎ≥¥ Ï†ëÍ∑ºÏù¥ Í±∞Î∂ÄÎêòÏóàÏäµÎãàÎã§. Î∏åÎùºÏö∞Ï†Ä ÏÑ§Ï†ïÏóêÏÑú ÏúÑÏπò Ï†ïÎ≥¥ Ï†ëÍ∑ºÏùÑ ÌóàÏö©Ìï¥Ï£ºÏÑ∏Ïöî.';
            break;
          case error.POSITION_UNAVAILABLE:
            errorMessage = 'ÏúÑÏπò Ï†ïÎ≥¥Î•º ÏÇ¨Ïö©Ìï† Ïàò ÏóÜÏäµÎãàÎã§.';
            break;
          case error.TIMEOUT:
            errorMessage = 'ÏúÑÏπò Ï†ïÎ≥¥ ÏöîÏ≤≠ ÏãúÍ∞ÑÏù¥ Ï¥àÍ≥ºÎêòÏóàÏäµÎãàÎã§.';
            break;
        }
        setError(errorMessage);
        setLoading(false);
      },
      {
        enableHighAccuracy: true,
        timeout: 10000,
        maximumAge: 300000 // 5Î∂Ñ
      }
    );
  };

  // ÌéòÏù¥ÏßÄ Î°úÎìú Ïãú ÏûêÎèôÏúºÎ°ú ÏúÑÏπò Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    getUserLocation();
  }, []);

  const renderStars = (rating) => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <span key={i} className={`star ${i <= rating ? 'filled' : ''}`}>
          ‚òÖ
        </span>
      );
    }
    return stars;
  };



  // Ï∞úÌïòÍ∏∞ Î≤ÑÌäº ÌÅ¥Î¶≠ Ìï∏Îì§Îü¨ (ÌÜ†Í∏Ä Í∏∞Îä•)
  const handleFavorite = (restaurant) => {
    if (isInFavorites(restaurant.id)) {
      removeFromFavorites(restaurant.id);
      alert('Ï∞ú Î™©Î°ùÏóêÏÑú Ï†úÍ±∞ÎêòÏóàÏäµÎãàÎã§! üëã');
    } else {
      addToFavorites(restaurant);
      alert('Ï∞ú Î™©Î°ùÏóê Ï∂îÍ∞ÄÎêòÏóàÏäµÎãàÎã§! üéâ');
    }
  };

  // ÏÉÅÏÑ∏Î≥¥Í∏∞ Î≤ÑÌäº ÌÅ¥Î¶≠ Ìï∏Îì§Îü¨
  const handleDetail = (restaurant) => {
    setSelectedRestaurant(restaurant);
    setShowModal(true);
  };

  // Î™®Îã¨ Îã´Í∏∞
  const closeModal = () => {
    setShowModal(false);
    setSelectedRestaurant(null);
  };

  // Í∏∏Ï∞æÍ∏∞ Î≤ÑÌäº ÌÅ¥Î¶≠ Ìï∏Îì§Îü¨
  const handleNavigate = (restaurant) => {
    const { lat, lng } = restaurant.position;
    if (lat && lng) {
      window.open(`https://map.kakao.com/link/to/${restaurant.name},${lat},${lng}`, '_blank');
    } else {
      alert('ÏúÑÏπò Ï†ïÎ≥¥Í∞Ä ÏóÜÏñ¥ Í∏∏Ï∞æÍ∏∞Î•º Ìï† Ïàò ÏóÜÏäµÎãàÎã§.');
    }
  };

  return (
    <div>
      <h2>üìç ÎÇ¥ Ï£ºÎ≥Ä ÎßõÏßë Ï∂îÏ≤ú</h2>
      <p>ÌòÑÏû¨ ÏúÑÏπòÎ•º Í∏∞Î∞òÏúºÎ°ú Í∞ÄÍπåÏö¥ ÎßõÏßëÏùÑ Ï∂îÏ≤úÌï¥ÎìúÎ¶ΩÎãàÎã§!</p>

      {loading && (
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>ÏúÑÏπòÎ•º ÌôïÏù∏ÌïòÍ≥† ÏûàÏäµÎãàÎã§...</p>
        </div>
      )}

      {error && (
        <div className="error-message">
          <p>‚ö†Ô∏è {error}</p>
          <button 
            className="location-btn"
            onClick={getUserLocation}
          >
            Îã§Ïãú ÏãúÎèÑ
          </button>
        </div>
      )}

      {userLocation && nearbyRestaurants.length > 0 && (
        <div className="nearby-grid">
          {nearbyRestaurants.map((restaurant, index) => (
            <div key={restaurant.id} className="nearby-card">
              <div className="nearby-header">
                <h4>{restaurant.name}</h4>
                <div className="distance">{restaurant.distance.toFixed(1)}km</div>
              </div>

              <div className="rating-section">
                <div className="stars">
                  {renderStars(restaurant.rating)}
                </div>
                <div className="rating-text">{restaurant.rating}</div>
                <div className="total-ratings">({restaurant.totalRatings || 0}Î™Ö)</div>
              </div>

              <div className="description">{restaurant.description}</div>

              <div className="restaurant-meta">
                <p>üìç {restaurant.address}</p>

                <p>üïí {restaurant.businessHours}</p>
                <p>üìû {restaurant.phone}</p>
                <p>üöó {restaurant.parking}</p>
              </div>



              <div className="nearby-actions">
                <button 
                  className={`favorite-btn ${isInFavorites(restaurant.id) ? 'favorited' : ''}`}
                  onClick={() => handleFavorite(restaurant)}
                >
                  {isInFavorites(restaurant.id) ? '‚ù§Ô∏è Ï∞úÎê®' : 'ü§ç ÎÇ¥ Î™©Î°ùÏóê Îã¥Í∏∞'}
                </button>
                <button 
                  className="detail-btn"
                  onClick={() => handleDetail(restaurant)}
                >
                  üìã ÏÉÅÏÑ∏Î≥¥Í∏∞
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* ÏÉÅÏÑ∏Î≥¥Í∏∞ Î™®Îã¨ */}
      {showModal && selectedRestaurant && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h2>{selectedRestaurant.name}</h2>
              <button className="modal-close" onClick={closeModal}>‚úï</button>
            </div>

            <div className="modal-body">
              <div className="restaurant-info">
                <div className="rating-section">
                  <div className="stars">
                    {renderStars(selectedRestaurant.rating)}
                  </div>
                  <div className="rating-text">{selectedRestaurant.rating}</div>
                  <div className="total-ratings">({selectedRestaurant.totalRatings || 0}Î™Ö)</div>
                </div>

                <div className="distance-info">
                  <p><strong>üìç Í±∞Î¶¨:</strong> {selectedRestaurant.distance.toFixed(1)}km</p>
                </div>

                <div className="restaurant-details">
                  <p><strong>üìç Ï£ºÏÜå:</strong> {selectedRestaurant.address}</p>

                  <p><strong>üïí ÏòÅÏóÖÏãúÍ∞Ñ:</strong> {selectedRestaurant.businessHours}</p>
                  <p><strong>üìû Ï†ÑÌôîÎ≤àÌò∏:</strong> {selectedRestaurant.phone}</p>
                  <p><strong>üöó Ï£ºÏ∞®:</strong> {selectedRestaurant.parking}</p>
                </div>

                <div className="restaurant-description">
                  <p><strong>üìù ÏÜåÍ∞ú:</strong></p>
                  <p>{selectedRestaurant.description}</p>
                </div>


              </div>
            </div>

            <div className="modal-actions">
              <button 
                className={`modal-btn favorite-btn ${isInFavorites(selectedRestaurant.id) ? 'favorited' : ''}`}
                onClick={() => handleFavorite(selectedRestaurant)}
              >
                {isInFavorites(selectedRestaurant.id) ? '‚ù§Ô∏è Ï∞úÎê®' : 'ü§ç Ï∞úÌïòÍ∏∞'}
              </button>
              <button 
                className="modal-btn navigate-btn"
                onClick={() => handleNavigate(selectedRestaurant)}
              >
                üó∫Ô∏è Í∏∏Ï∞æÍ∏∞
              </button>
              <button 
                className="modal-btn close-btn"
                onClick={closeModal}
              >
                Îã´Í∏∞
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default LocationBasedRecommendation;
