  // Î∞±ÏóîÎìú ÏÑúÎ≤Ñ ÏÉÅÌÉúÏôÄ DB Ïó∞Í≤∞ ÏÉÅÌÉú ÌôïÏù∏
  useEffect(() => {
    const checkServerStatus = async () => {
      try {
        // Î∞±ÏóîÎìú ÏÑúÎ≤Ñ ÏÉÅÌÉú ÌôïÏù∏
        const serverResponse = await fetch('http://localhost:8080/api/restaurants');
        const isServerRunning = serverResponse.ok;

        // Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ Ïó∞Í≤∞ ÏÉÅÌÉú ÌôïÏù∏ (Î†àÏä§ÌÜ†Îûë Îç∞Ïù¥ÌÑ∞Í∞Ä ÏûàÎäîÏßÄ ÌôïÏù∏)
        let isDbConnected = false;
        let restaurantCount = 0;

        if (isServerRunning) {
          const data = await serverResponse.json();
          restaurantCount = data.length;
          isDbConnected = restaurantCount > 0;
        }

        // ÏÉÅÌÉú ÏïåÎ¶º
        let statusMessage = '';
        if (isServerRunning && isDbConnected) {
          statusMessage = `‚úÖ ÏÑúÎ≤Ñ ÏÉÅÌÉú: Ï†ïÏÉÅ\n‚úÖ Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ Ïó∞Í≤∞: Ï†ïÏÉÅ\nüìä Î†àÏä§ÌÜ†Îûë Îç∞Ïù¥ÌÑ∞: ${restaurantCount}Í∞ú`;
        } else if (isServerRunning && !isDbConnected) {
          statusMessage = `‚úÖ ÏÑúÎ≤Ñ ÏÉÅÌÉú: Ï†ïÏÉÅ\n‚ùå Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ Ïó∞Í≤∞: Ïã§Ìå®\nüìä Î†àÏä§ÌÜ†Îûë Îç∞Ïù¥ÌÑ∞: ${restaurantCount}Í∞ú`;
        } else {
          statusMessage = `‚ùå ÏÑúÎ≤Ñ ÏÉÅÌÉú: Ïã§Ìå®\n‚ùå Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ Ïó∞Í≤∞: Ïã§Ìå®\nüìä Î†àÏä§ÌÜ†Îûë Îç∞Ïù¥ÌÑ∞: 0Í∞ú`;
        }

        alert(statusMessage);

        // Î†àÏä§ÌÜ†Îûë Îç∞Ïù¥ÌÑ∞ Î°úÎìú
        if (isServerRunning) {
          const data = await serverResponse.json();

          // Î∞©Î¨∏ Ïàò Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
          const visitCountsResponse = await fetch('http://localhost:8080/api/visits/count/all');
          let visitCounts = {};
          if (visitCountsResponse.ok) {
            visitCounts = await visitCountsResponse.json();
          }

          // ÌèâÏ†êÏù¥ ÎÜíÏùÄ ÏàúÏúºÎ°ú Ï†ïÎ†¨ÌïòÏó¨ ÏÉÅÏúÑ 3Í∞ú ÏÑ†ÌÉù
          const sortedRestaurants = data
            .sort((a, b) => b.rating - a.rating)
            .slice(0, 3)
            .map(restaurant => ({
              ...restaurant,
              totalRatings: visitCounts[restaurant.id] || 0,
              price: "2ÎßåÏõê~5ÎßåÏõê",
              businessHours: "11:00 - 22:00",
              position: { 
                lat: restaurant.latitude || 37.5665, 
                lng: restaurant.longitude || 126.9780 
              }
            }));
          setTopRestaurants(sortedRestaurants);
        } else {
          setError('ÏÑúÎ≤ÑÏóê Ïó∞Í≤∞Ìï† Ïàò ÏóÜÏäµÎãàÎã§.');
        }
      } catch (err) {
        console.error('Failed to check server status:', err);
        alert('‚ùå ÏÑúÎ≤Ñ ÏÉÅÌÉú ÌôïÏù∏ Ïã§Ìå®\n‚ùå Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ Ïó∞Í≤∞: ÌôïÏù∏ Î∂àÍ∞Ä\nüìä Î†àÏä§ÌÜ†Îûë Îç∞Ïù¥ÌÑ∞: 0Í∞ú');
        setError('ÏÑúÎ≤ÑÏóê Ïó∞Í≤∞Ìï† Ïàò ÏóÜÏäµÎãàÎã§.');
      } finally {
        setLoading(false);
      }
    };

    checkServerStatus();
  }, []);

  const features = [
    {
      icon: 'üçΩÔ∏è',
      title: 'Îã§ÏñëÌïú ÎßõÏßë',
      description: 'ÌïúÏãù, ÏñëÏãù, ÏùºÏãù, Ï§ëÏãù Îì± Îã§ÏñëÌïú Ï¢ÖÎ•òÏùò ÎßõÏßëÏùÑ ÏÜåÍ∞úÌï©ÎãàÎã§.'
    },
    {
      icon: '‚≠ê',
      title: 'Ïã§ÏãúÍ∞Ñ ÌèâÏ†ê',
      description: 'ÏÇ¨Ïö©ÏûêÎì§Ïù¥ ÏßÅÏ†ë ÎÇ®Í∏¥ ÌèâÏ†êÏúºÎ°ú Ïã†Î¢∞Ìï† Ïàò ÏûàÎäî ÎßõÏßë Ï†ïÎ≥¥Î•º Ï†úÍ≥µÌï©ÎãàÎã§.'
    },
    {
      icon: 'üó∫Ô∏è',
      title: 'ÏßÄÎèÑ ÏÑúÎπÑÏä§',
      description: 'ÏßÄÎèÑÏóêÏÑú ÎßõÏßë ÏúÑÏπòÎ•º ÌïúÎààÏóê ÌôïÏù∏ÌïòÍ≥† Ìé∏Î¶¨ÌïòÍ≤å Ï∞æÏïÑÍ∞à Ïàò ÏûàÏäµÎãàÎã§.'
    },
    {
      icon: 'üîç',
      title: 'Ïπ¥ÌÖåÍ≥†Î¶¨Î≥Ñ Í≤ÄÏÉâ',
      description: 'ÏùåÏãù Ï¢ÖÎ•òÎ≥ÑÎ°ú ÌïÑÌÑ∞ÎßÅÌïòÏó¨ ÏõêÌïòÎäî ÎßõÏßëÏùÑ ÏâΩÍ≤å Ï∞æÏùÑ Ïàò ÏûàÏäµÎãàÎã§.'
    }
  ];



  const renderStars = (rating) => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <span key={i} className={`star ${i <= rating ? 'filled' : ''}`}>
          ‚òÖ
        </span>
      );
    }
    return stars;
  };

  // Ï∞úÌïòÍ∏∞ Î≤ÑÌäº ÌÅ¥Î¶≠ Ìï∏Îì§Îü¨ (ÌÜ†Í∏Ä Í∏∞Îä•)
  const handleFavorite = (restaurant) => {
    if (isInFavorites(restaurant.id)) {
      removeFromFavorites(restaurant.id);
    } else {
      addToFavorites(restaurant);
    }
  };

  // ÏÉÅÏÑ∏Î≥¥Í∏∞ Î≤ÑÌäº ÌÅ¥Î¶≠ Ìï∏Îì§Îü¨
  const handleDetail = (restaurant) => {
    setSelectedRestaurant(restaurant);
    setShowModal(true);
  };

  // Î™®Îã¨ Îã´Í∏∞
  const closeModal = () => {
    setShowModal(false);
    setSelectedRestaurant(null);
  };

  // Í∏∏Ï∞æÍ∏∞ Î≤ÑÌäº ÌÅ¥Î¶≠ Ìï∏Îì§Îü¨
  const handleNavigate = (restaurant) => {
    const { lat, lng } = restaurant.position;
    if (lat && lng) {
      window.open(`https://map.kakao.com/link/to/${restaurant.name},${lat},${lng}`, '_blank');
    } else {
      alert('ÏúÑÏπò Ï†ïÎ≥¥Í∞Ä ÏóÜÏñ¥ Í∏∏Ï∞æÍ∏∞Î•º Ìï† Ïàò ÏóÜÏäµÎãàÎã§.');
    }
  };

  return (
    <div className="home-container">
      {/* Ìó§Îçî ÏÑπÏÖò */}
      <section className="home-header">
        <h1>üçΩÔ∏è Ïù∏ÏäêÎû≠ ÎßõÏßë Í∞ÄÏù¥ÎìúÏóê Ïò§Ïã† Í≤ÉÏùÑ ÌôòÏòÅÌï©ÎãàÎã§!</h1>
        <p>ÏµúÍ≥†Ïùò ÎßõÏßëÎì§ÏùÑ Î∞úÍ≤¨ÌïòÍ≥†, ÌèâÏ†êÏùÑ ÎÇ®Í∏∞Í≥†, ÏßÄÎèÑÏóêÏÑú ÏúÑÏπòÎ•º ÌôïÏù∏ÌïòÏÑ∏Ïöî.</p>
        {!currentUser ? (
          <div className="home-actions">
            <Link to="/signup" className="home-btn">‚úçÔ∏è ÌöåÏõêÍ∞ÄÏûÖ</Link>
            <Link to="/login" className="home-btn secondary">üîë Î°úÍ∑∏Ïù∏</Link>
          </div>
        ) : (
          <div className="home-actions">
            <Link to="/restaurants" className="home-btn">üìã ÎßõÏßë ÎëòÎü¨Î≥¥Í∏∞</Link>
            <Link to="/map" className="home-btn secondary">üó∫Ô∏è ÏßÄÎèÑ Î≥¥Í∏∞</Link>
          </div>
        )}
      </section>

      {/* ÏúÑÏπò Í∏∞Î∞ò Ï∂îÏ≤ú ÏÑπÏÖò */}
      <section className="location-recommendations">
        <LocationBasedRecommendation />
      </section>

      {/* TOP 3 ÎßõÏßë ÏÑπÏÖò */}
      <section className="recommendations-section">
        <h2>Ï£ºÏù∏Ïû• Ï∂îÏ≤ú ÏãùÎãπ TOP 3</h2>
        {loading ? (
          <div className="loading">Î°úÎî© Ï§ë...</div>
        ) : error ? (
          <div className="error">{error}</div>
        ) : (
          <div className="recommendations-grid">
            {topRestaurants.map((restaurant, index) => (
            <div key={restaurant.id} className="recommendation-card">
              <div className="recommendation-header">
                <h3>{restaurant.name}</h3>
                <div className="rank-badge">#{index + 1}</div>
              </div>

              <div className="rating-section">
                <div className="stars">
                  {renderStars(restaurant.rating)}
                </div>
                <div className="rating-text">{restaurant.rating}</div>
                <div className="total-ratings">({restaurant.totalRatings}Î™Ö Î∞©Î¨∏)</div>
              </div>

              <div className="description">{restaurant.description}</div>

              <div className="restaurant-meta">
                <p>üìç {restaurant.address}</p>

                <p>üïí {restaurant.businessHours}</p>
                <p>üìû {restaurant.phone}</p>
                <p>üöó {restaurant.parking}</p>
              </div>

              <div className="recommendation-actions">
                <button 
                  className={`favorite-btn ${isInFavorites(restaurant.id) ? 'favorited' : ''}`}
                  onClick={() => handleFavorite(restaurant)}
                >
                  {isInFavorites(restaurant.id) ? '‚ù§Ô∏è Ï∞úÎê®' : 'ü§ç ÎÇ¥ Î™©Î°ùÏóê Îã¥Í∏∞'}
                </button>
                <button 
                  className="detail-btn"
                  onClick={() => handleDetail(restaurant)}
                >
                  üìã ÏÉÅÏÑ∏Î≥¥Í∏∞
                </button>
              </div>
            </div>
          ))}
        </div>
        )}
      </section>

      {/* ÏÉÅÏÑ∏Î≥¥Í∏∞ Î™®Îã¨ */}
      {showModal && selectedRestaurant && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h2>{selectedRestaurant.name}</h2>
              <button className="modal-close" onClick={closeModal}>‚úï</button>
            </div>

            <div className="modal-body">
              <div className="restaurant-info">
                <div className="rating-section">
                  <div className="stars">
                    {renderStars(selectedRestaurant.rating)}
                  </div>
                  <div className="rating-text">{selectedRestaurant.rating}</div>
                  <div className="total-ratings">({selectedRestaurant.totalRatings}Î™Ö)</div>
                </div>

                <div className="restaurant-details">
                  <p><strong>üìç Ï£ºÏÜå:</strong> {selectedRestaurant.address}</p>

                  <p><strong>üïí ÏòÅÏóÖÏãúÍ∞Ñ:</strong> {selectedRestaurant.businessHours}</p>
                  <p><strong>üìû Ï†ÑÌôîÎ≤àÌò∏:</strong> {selectedRestaurant.phone}</p>
                  <p><strong>üöó Ï£ºÏ∞®:</strong> {selectedRestaurant.parking}</p>
                </div>

                <div className="restaurant-description">
                  <p><strong>üìù ÏÜåÍ∞ú:</strong></p>
                  <p>{selectedRestaurant.description}</p>
                </div>
              </div>
            </div>

            <div className="modal-actions">
              <button 
                className={`modal-btn favorite-btn ${isInFavorites(selectedRestaurant.id) ? 'favorited' : ''}`}
                onClick={() => handleFavorite(selectedRestaurant)}
              >
                {isInFavorites(selectedRestaurant.id) ? '‚ù§Ô∏è Ï∞úÎê®' : 'ü§ç Ï∞úÌïòÍ∏∞'}
              </button>
              <button 
                className="modal-btn navigate-btn"
                onClick={() => handleNavigate(selectedRestaurant)}
              >
                üó∫Ô∏è Í∏∏Ï∞æÍ∏∞
              </button>
              <button 
                className="modal-btn close-btn"
                onClick={closeModal}
              >
                Îã´Í∏∞
