import { supabase } from './supabase';
import { restaurantData } from '../data/restaurantData';

// 레스토랑 관련 API
export const restaurantAPI = {
  // 모든 레스토랑 조회
  getAll: async () => {
    try {
      const { data, error } = await supabase
        .from('restaurants')
        .select('*');

      if (error) {
        console.warn('Supabase 연결 실패, 기본 데이터 사용:', error);
        // 기본 데이터 반환
        return { 
          data: [
            {
              id: 1,
              name: "맛있는 한식집",
              cuisine: "한식",
              rating: 4.5,
              address: "서울시 강남구 테헤란로 123",
              description: "전통 한식 전문점입니다.",
              latitude: 37.5665,
              longitude: 126.9780,
              phone: "02-1234-5678",
              parking: "주차 가능",
              businessHours: "11:00 - 22:00"
            },
            {
              id: 2,
              name: "신선한 일식집",
              cuisine: "일식",
              rating: 4.3,
              address: "서울시 강남구 역삼동 456",
              description: "신선한 회와 초밥 전문점입니다.",
              latitude: 37.5668,
              longitude: 126.9785,
              phone: "02-2345-6789",
              parking: "주차 불가",
              businessHours: "11:30 - 22:30"
            },
            {
              id: 3,
              name: "맛있는 중식집",
              cuisine: "중식",
              rating: 4.2,
              address: "서울시 강남구 삼성동 789",
              description: "정통 중국 요리 전문점입니다.",
              latitude: 37.5670,
              longitude: 126.9790,
              phone: "02-3456-7890",
              parking: "주차 가능",
              businessHours: "11:00 - 21:00"
            }
          ]
        };
      }
      return { data: data || [] };
    } catch (error) {
      console.warn('API 호출 실패, 기본 데이터 사용:', error);
      return { 
        data: restaurantData.map(restaurant => ({
          id: restaurant.id,
          name: restaurant.name,
          cuisine: restaurant.category,
          rating: restaurant.rating,
          address: restaurant.address,
          description: restaurant.description,
          latitude: restaurant.position.lat,
          longitude: restaurant.position.lng,
          phone: restaurant.phone,
          parking: restaurant.parking,
          businessHours: restaurant.hours
        }))
      };
    }
  },

  // ID로 레스토랑 조회
  getById: async (id) => {
    const { data, error } = await supabase
      .from('restaurants')
      .select('*')
      .eq('id', id)
      .single();

    if (error) throw error;
    return { data };
  },

  // 새 레스토랑 추가
  create: async (restaurant) => {
    const { data, error } = await supabase
      .from('restaurants')
      .insert(restaurant)
      .select()
      .single();

    if (error) throw error;
    return { data };
  },

  // 레스토랑 정보 수정
  update: async (id, restaurant) => {
    const { data, error } = await supabase
      .from('restaurants')
      .update(restaurant)
      .eq('id', id)
      .select()
      .single();

    if (error) throw error;
    return { data };
  },

  // 레스토랑 삭제
  delete: async (id) => {
    const { error } = await supabase
      .from('restaurants')
      .delete()
      .eq('id', id);

    if (error) throw error;
    return { success: true };
  },

  // 요리 타입으로 검색
  getByCuisine: async (cuisine) => {
    const { data, error } = await supabase
      .from('restaurants')
      .select('*')
      .eq('cuisine', cuisine);

    if (error) throw error;
    return { data };
  },

  // 평점으로 검색
  getByRating: async (rating) => {
    const { data, error } = await supabase
      .from('restaurants')
      .select('*')
      .gte('rating', rating);

    if (error) throw error;
    return { data };
  },

  // 키워드로 검색
  search: async (keyword) => {
    const { data, error } = await supabase
      .from('restaurants')
      .select('*')
      .or(`name.ilike.%${keyword}%,description.ilike.%${keyword}%`);

    if (error) throw error;
    return { data };
  },

  // 근처 레스토랑 검색
  getNearby: async (lat, lng, radius = 0.01) => {
    const { data, error } = await supabase
      .from('restaurants')
      .select('*')
      .not('latitude', 'is', null)
      .not('longitude', 'is', null);

    if (error) throw error;

    // 클라이언트에서 거리 계산
    const nearby = data.filter(restaurant => {
      const distance = Math.sqrt(
        Math.pow(restaurant.latitude - lat, 2) + 
        Math.pow(restaurant.longitude - lng, 2)
      );
      return distance <= radius;
    });

    return { data: nearby };
  },

  // 랜덤 레스토랑 추천
  getRandom: async () => {
    const { data, error } = await supabase
      .from('restaurants')
      .select('*');

    if (error) throw error;

    if (data && data.length > 0) {
      const randomIndex = Math.floor(Math.random() * data.length);
      return { data: data[randomIndex] };
    }

    return { data: null };
  },
};

export default restaurantAPI;
