import React, { useState, useEffect } from 'react';
import { useFavorites } from '../context/FavoriteContext';
import { useVisit } from '../context/VisitContext';
import { restaurantAPI } from '../services/api';
import './RandomRecommendation.css';

const RandomRecommendation = () => {
  const [currentRecommendation, setCurrentRecommendation] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [showFilters, setShowFilters] = useState(false);
  const [restaurants, setRestaurants] = useState([]);
  const [filterOptions, setFilterOptions] = useState({
    categories: ['all'],
    regions: ['all'],
    priceRanges: ['all']
  });
  const [filters, setFilters] = useState({
    category: 'all',
    region: 'all',
    maxDistance: 'all',
    mood: 'all'
  });
  const [userLocation, setUserLocation] = useState(null);
  const [locationError, setLocationError] = useState(null);
  const { addToFavorites, removeFromFavorites, isInFavorites } = useFavorites();
  const { getRestaurantVisitStatus } = useVisit();

  // ÏÇ¨Ïö©Ïûê ÏúÑÏπò Í∞ÄÏ†∏Ïò§Í∏∞
  const getUserLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUserLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude
          });
          setLocationError(null);
        },
        (error) => {
          console.error('ÏúÑÏπò Ï†ïÎ≥¥Î•º Í∞ÄÏ†∏Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§:', error);
          setLocationError('ÏúÑÏπò Ï†ïÎ≥¥Î•º Í∞ÄÏ†∏Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§. Í±∞Î¶¨ ÌïÑÌÑ∞Í∞Ä ÎπÑÌôúÏÑ±ÌôîÎê©ÎãàÎã§.');
        }
      );
    } else {
      setLocationError('Ïù¥ Î∏åÎùºÏö∞Ï†ÄÎäî ÏúÑÏπò Ï†ïÎ≥¥Î•º ÏßÄÏõêÌïòÏßÄ ÏïäÏäµÎãàÎã§.');
    }
  };

  // Î∞±ÏóîÎìúÏóêÏÑú Î†àÏä§ÌÜ†Îûë Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    const fetchRestaurants = async () => {
      try {
        const response = await restaurantAPI.getAll();
        const restaurantsWithExtraData = response.data.map(restaurant => ({
          ...restaurant,
          category: restaurant.cuisine, // cuisineÏùÑ categoryÎ°ú Îß§Ìïë
          price: "2ÎßåÏõê~5ÎßåÏõê", // ÏûÑÏãú Îç∞Ïù¥ÌÑ∞
          parking: "Ï£ºÏ∞® Í∞ÄÎä•", // ÏûÑÏãú Îç∞Ïù¥ÌÑ∞
          position: { lat: restaurant.latitude, lng: restaurant.longitude }
        }));
        setRestaurants(restaurantsWithExtraData);

        // ÌïÑÌÑ∞ ÏòµÏÖò ÎèôÏ†Å ÏÉùÏÑ±
        generateFilterOptions(restaurantsWithExtraData);
      } catch (err) {
        console.error('Failed to fetch restaurants:', err);
      }
    };

    fetchRestaurants();
    getUserLocation(); // ÏÇ¨Ïö©Ïûê ÏúÑÏπò Í∞ÄÏ†∏Ïò§Í∏∞
  }, []);

  // Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ÏóêÏÑú ÌïÑÌÑ∞ ÏòµÏÖò ÎèôÏ†Å ÏÉùÏÑ±
  const generateFilterOptions = (restaurants) => {
    if (!restaurants.length) return;

    // Ïπ¥ÌÖåÍ≥†Î¶¨ ÏòµÏÖò ÏÉùÏÑ±
    const categories = ['all', ...new Set(restaurants.map(r => r.cuisine))];

    // ÏßÄÏó≠ ÏòµÏÖò ÏÉùÏÑ± (Ïãú/Íµ¨ Îã®ÏúÑÎ°ú Í∑∏Î£πÌôî)
    const regions = ['all'];
    const regionMap = {};

    restaurants.forEach(restaurant => {
      const addressParts = restaurant.address.split(' ');
      if (addressParts.length >= 2) {
        const region = `${addressParts[0]} ${addressParts[1]}`;
        if (!regionMap[region]) {
          regionMap[region] = 0;
          regions.push(region);
        }
        regionMap[region]++;
      }
    });



    // Í±∞Î¶¨ ÏòµÏÖò
    const distanceOptions = [
      { value: 'all', label: 'Í±∞Î¶¨ Î¨¥Í¥Ä' },
      { value: '1', label: '1km Ïù¥ÎÇ¥' },
      { value: '2', label: '2km Ïù¥ÎÇ¥' },
      { value: '3', label: '3km Ïù¥ÎÇ¥' },
      { value: '5', label: '5km Ïù¥ÎÇ¥' }
    ];

    // Î∂ÑÏúÑÍ∏∞ ÏòµÏÖò
    const moodOptions = [
      { value: 'all', label: 'Î∂ÑÏúÑÍ∏∞ Î¨¥Í¥Ä' },
      { value: 'solo', label: 'ÌòºÎ∞• Í∞ÄÎä•' },
      { value: 'date', label: 'Îç∞Ïù¥Ìä∏ ÏΩîÏä§' },
      { value: 'group', label: 'Îã®Ï≤¥ Î™®ÏûÑ' },
      { value: 'family', label: 'Í∞ÄÏ°± Î™®ÏûÑ' }
    ];

    setFilterOptions({
      categories,
      regions,
      distanceOptions,
      moodOptions
    });
  };

  // Í±∞Î¶¨ Í≥ÑÏÇ∞ Ìï®Ïàò
  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // ÏßÄÍµ¨Ïùò Î∞òÏßÄÎ¶Ñ (km)
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = 
      Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * 
      Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c; // km
  };



  // ÌïÑÌÑ∞ÎßÅÎêú Î†àÏä§ÌÜ†Îûë Î™©Î°ù ÏÉùÏÑ±
  const getFilteredRestaurants = () => {
    let filtered = restaurants;

    // Ïπ¥ÌÖåÍ≥†Î¶¨ ÌïÑÌÑ∞
    if (filters.category !== 'all') {
      filtered = filtered.filter(restaurant => restaurant.cuisine === filters.category);
    }

    // ÏßÄÏó≠ ÌïÑÌÑ∞
    if (filters.region !== 'all') {
      filtered = filtered.filter(restaurant => {
        return restaurant.address.includes(filters.region);
      });
    }

    // Í±∞Î¶¨ ÌïÑÌÑ∞
    if (filters.maxDistance !== 'all' && userLocation) {
      const maxDistance = parseFloat(filters.maxDistance);
      filtered = filtered.filter(restaurant => {
        if (restaurant.latitude && restaurant.longitude) {
          const distance = calculateDistance(
            userLocation.lat,
            userLocation.lng,
            restaurant.latitude,
            restaurant.longitude
          );
          return distance <= maxDistance;
        }
        return false; // Ï¢åÌëúÍ∞Ä ÏóÜÎäî Î†àÏä§ÌÜ†ÎûëÏùÄ Ï†úÏô∏
      });
    }



    // Î∂ÑÏúÑÍ∏∞ ÌïÑÌÑ∞ (Ïã§Ï†ú Îç∞Ïù¥ÌÑ∞ Í∏∞Î∞ò)
    if (filters.mood !== 'all') {
      if (filters.mood === 'solo') {
        filtered = filtered.filter(restaurant => 
          restaurant.cuisine === 'ÌïúÏãù' || restaurant.cuisine === 'Î∂ÑÏãù'
        );
      } else if (filters.mood === 'date') {
        filtered = filtered.filter(restaurant => 
          restaurant.cuisine === 'ÏñëÏãù' || restaurant.cuisine === 'Ïπ¥Ìéò' || restaurant.cuisine === 'ÏùºÏãù'
        );
      } else if (filters.mood === 'group') {
        filtered = filtered.filter(restaurant => 
          restaurant.cuisine === 'Ï§ëÏãù' || restaurant.cuisine === 'ÌïúÏãù'
        );
      } else if (filters.mood === 'family') {
        filtered = filtered.filter(restaurant => 
          restaurant.cuisine === 'ÌïúÏãù' || restaurant.cuisine === 'Ï§ëÏãù'
        );
      }
    }

    return filtered;
  };

  // ÎûúÎç§ Ï∂îÏ≤ú ÏÉùÏÑ±
  const generateRandomRecommendation = async () => {
    setIsLoading(true);

    try {
      // ÌïÑÌÑ∞ÎßÅÎêú Î†àÏä§ÌÜ†Îûë Î™©Î°ù Í∞ÄÏ†∏Ïò§Í∏∞
      const filteredRestaurants = getFilteredRestaurants();

      if (filteredRestaurants.length === 0) {
        alert('ÏÑ†ÌÉùÌïú ÌïÑÌÑ∞ Ï°∞Í±¥Ïóê ÎßûÎäî Î†àÏä§ÌÜ†ÎûëÏù¥ ÏóÜÏäµÎãàÎã§. ÌïÑÌÑ∞Î•º Ï°∞Ï†ïÌï¥Ï£ºÏÑ∏Ïöî.');
        setIsLoading(false);
        return;
      }

      // ÌïÑÌÑ∞ÎßÅÎêú Î™©Î°ùÏóêÏÑú ÎûúÎç§ ÏÑ†ÌÉù
      const randomIndex = Math.floor(Math.random() * filteredRestaurants.length);
      const randomRestaurant = filteredRestaurants[randomIndex];

      // Ï∂îÍ∞Ä Îç∞Ïù¥ÌÑ∞ Îß§Ìïë
      const restaurantWithExtraData = {
        ...randomRestaurant,
        category: randomRestaurant.cuisine,
        price: "2ÎßåÏõê~5ÎßåÏõê",
        parking: "Ï£ºÏ∞® Í∞ÄÎä•",
        position: { lat: randomRestaurant.latitude, lng: randomRestaurant.longitude }
      };

      setCurrentRecommendation(restaurantWithExtraData);
    } catch (err) {
      console.error('Failed to get random restaurant:', err);
      alert('ÎûúÎç§ Ï∂îÏ≤úÏùÑ Í∞ÄÏ†∏Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    } finally {
      setIsLoading(false);
    }
  };

  // ÌïÑÌÑ∞ Î≥ÄÍ≤Ω Ìï∏Îì§Îü¨
  const handleFilterChange = (filterType, value) => {
    setFilters(prev => ({
      ...prev,
      [filterType]: value
    }));
  };

  // Ï¢ãÏïÑÏöî Î≤ÑÌäº ÌÅ¥Î¶≠ (ÌÜ†Í∏Ä Í∏∞Îä•)
  const handleLike = () => {
    if (currentRecommendation) {
      if (isInFavorites(currentRecommendation.id)) {
        removeFromFavorites(currentRecommendation.id);
      } else {
        addToFavorites(currentRecommendation);
      }
    }
  };

  // Í∏∏Ï∞æÍ∏∞ Î≤ÑÌäº ÌÅ¥Î¶≠
  const handleNavigate = () => {
    if (currentRecommendation) {
      const { lat, lng } = currentRecommendation.position;
      window.open(`https://map.kakao.com/link/to/${currentRecommendation.name},${lat},${lng}`, '_blank');
    }
  };

  // Î≥ÑÏ†ê Î†åÎçîÎßÅ
  const renderStars = (rating) => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <span key={i} className={`star ${i <= rating ? 'filled' : ''}`}>
          ‚òÖ
        </span>
      );
    }
    return stars;
  };

  // Ïπ¥ÌÖåÍ≥†Î¶¨ ÏïÑÏù¥ÏΩò
  const getCategoryIcon = (category) => {
    const icons = {
      'ÌïúÏãù': 'üçö',
      'ÏñëÏãù': 'üçù',
      'ÏùºÏãù': 'üç£',
      'Ï§ëÏãù': 'ü•¢',
      'Ïπ¥Ìéò': '‚òï',
      'ÎîîÏ†ÄÌä∏': 'üç∞',
      'Î∂ÑÏãù': 'üç°',
      'Ïà†': 'üç∫',
      'ÌÉúÍµ≠': 'üçú'
    };
    return icons[category] || 'üçΩÔ∏è';
  };

  return (
    <div className="random-recommendation-container">
      <div className="recommendation-header">
        <h1>üé≤ Ïò§Îäò Î≠ê Î®πÏßÄ?</h1>
        <p>Ïö¥Î™ÖÏùò ÎßõÏßëÏùÑ Ï∞æÏïÑÎ≥¥ÏÑ∏Ïöî!</p>
      </div>

      <div className="recommendation-content">
        {!currentRecommendation && !isLoading && (
          <div className="start-section">
            <div className="dice-container">
              <div className="dice">üé≤</div>
              <button 
                className="random-button"
                onClick={generateRandomRecommendation}
              >
                ÎûúÎç§ Ï∂îÏ≤ú!
              </button>
            </div>

            <button 
              className="filter-toggle-btn"
              onClick={() => setShowFilters(!showFilters)}
            >
              {showFilters ? 'ÌïÑÌÑ∞ Ïà®Í∏∞Í∏∞' : 'ÌïÑÌÑ∞ ÏÑ§Ï†ï'}
            </button>

            {!userLocation && (
              <button 
                className="location-btn"
                onClick={getUserLocation}
              >
                üìç ÏúÑÏπò ÏÑ§Ï†ï
              </button>
            )}
          </div>
        )}

        {showFilters && (
          <div className="filters-section">
            <h3>üîç ÌïÑÌÑ∞ ÏÑ§Ï†ï</h3>

            <div className="filter-group">
              <label>ÏùåÏãù Ï¢ÖÎ•ò:</label>
              <select 
                value={filters.category} 
                onChange={(e) => handleFilterChange('category', e.target.value)}
              >
                {filterOptions.categories.map(cat => (
                  <option key={cat} value={cat}>
                    {cat === 'all' ? 'Ï†ÑÏ≤¥' : cat}
                  </option>
                ))}
              </select>
            </div>

            <div className="filter-group">
              <label>ÏßÄÏó≠:</label>
              <select 
                value={filters.region} 
                onChange={(e) => handleFilterChange('region', e.target.value)}
              >
                {filterOptions.regions.map(region => (
                  <option key={region} value={region}>
                    {region === 'all' ? 'Ï†ÑÏ≤¥ ÏßÄÏó≠' : region}
                  </option>
                ))}
              </select>
            </div>

            <div className="filter-group">
              <label>ÏµúÎåÄ Í±∞Î¶¨:</label>
              <select 
                value={filters.maxDistance} 
                onChange={(e) => handleFilterChange('maxDistance', e.target.value)}
                disabled={!userLocation}
              >
                <option value="all">Í±∞Î¶¨ Î¨¥Í¥Ä</option>
                <option value="1">1km Ïù¥ÎÇ¥</option>
                <option value="2">2km Ïù¥ÎÇ¥</option>
                <option value="3">3km Ïù¥ÎÇ¥</option>
                <option value="5">5km Ïù¥ÎÇ¥</option>
                <option value="10">10km Ïù¥ÎÇ¥</option>
              </select>
              {!userLocation && (
                <small className="location-warning">
                  üìç ÏúÑÏπò Ï†ïÎ≥¥Í∞Ä ÌïÑÏöîÌï©ÎãàÎã§. Î∏åÎùºÏö∞Ï†ÄÏóêÏÑú ÏúÑÏπò Ï†ëÍ∑ºÏùÑ ÌóàÏö©Ìï¥Ï£ºÏÑ∏Ïöî.
                </small>
              )}
              {locationError && (
                <small className="location-error">
                  ‚ö†Ô∏è {locationError}
                </small>
              )}
            </div>



            <div className="filter-group">
              <label>Î∂ÑÏúÑÍ∏∞:</label>
              <select 
                value={filters.mood} 
                onChange={(e) => handleFilterChange('mood', e.target.value)}
              >
                {filterOptions.moodOptions.map(option => (
                  <option key={option.value} value={option.value}>
                    {option.label}
                  </option>
                ))}
              </select>
            </div>
          </div>
        )}

        {isLoading && (
          <div className="loading-section">
            <div className="loading-animation">
              <div className="loading-dice">üé≤</div>
              <div className="loading-text">Ïö¥Î™ÖÏùò ÎßõÏßëÏùÑ Ï∞æÎäî Ï§ë...</div>
              <div className="loading-countdown">3... 2... 1...</div>
            </div>
          </div>
        )}

        {currentRecommendation && !isLoading && (
          <div className="recommendation-result">
            <div className="result-header">
              <h2>üçú Ïò§ÎäòÏùò Ï∂îÏ≤ú!</h2>
            </div>

            <div className="restaurant-card">
              <div className="restaurant-header">
                <h3>{currentRecommendation.name}</h3>
                <span className="category-badge">
                  {getCategoryIcon(currentRecommendation.category)} {currentRecommendation.category}
                </span>
              </div>

              <div className="restaurant-rating">
                <div className="stars">
                  {renderStars(currentRecommendation.rating)}
                </div>
                <span className="rating-text">{currentRecommendation.rating}</span>
              </div>

              <div className="restaurant-info">
                <p className="address">üìç {currentRecommendation.address}</p>
                {userLocation && currentRecommendation.latitude && currentRecommendation.longitude && (
                  <p className="distance">
                    üìè Í±∞Î¶¨: {calculateDistance(
                      userLocation.lat,
                      userLocation.lng,
                      currentRecommendation.latitude,
                      currentRecommendation.longitude
                    ).toFixed(1)}km
                  </p>
                )}
                <p className="price">üí∞ {currentRecommendation.price}</p>
                <p className="hours">üïí {currentRecommendation.hours}</p>
                <p className="phone">üìû {currentRecommendation.phone}</p>
                <p className="parking">üöó {currentRecommendation.parking}</p>
              </div>

              <div className="restaurant-description">
                <p>{currentRecommendation.description}</p>
              </div>

              <div className="action-buttons">
                <button 
                  className="action-btn dislike-btn"
                  onClick={generateRandomRecommendation}
                >
                  üîÑ Îã§Î•∏Í±∏Î°ú!
                </button>
                <button 
                  className={`action-btn like-btn ${isInFavorites(currentRecommendation.id) ? 'favorited' : ''}`}
                  onClick={handleLike}
                >
                  {isInFavorites(currentRecommendation.id) ? '‚ù§Ô∏è Ï∞úÎê®' : 'ü§ç Ï¢ãÏïÑÏöî!'}
                </button>
                <button 
                  className="action-btn navigate-btn"
                  onClick={handleNavigate}
                >
                  üó∫Ô∏è Í∏∏Ï∞æÍ∏∞
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default RandomRecommendation;
