  const categories = ['all', 'í•œì‹', 'ì¤‘ì‹', 'ì¼ì‹', 'ì–‘ì‹', 'ë¶„ì‹', 'íƒœêµ­', 'ìˆ ', 'ì¹´í˜', 'ë””ì €íŠ¸'];

