      'í•œì‹': 'ğŸš',
      'ì–‘ì‹': 'ğŸ',
      'ì¼ì‹': 'ğŸ£',
      'ì¤‘ì‹': 'ğŸ¥¢',
      'ì¹´í˜': 'â˜•',
      'ë””ì €íŠ¸': 'ğŸ°',
      'ë¶„ì‹': 'ğŸ¡',
      'ìˆ ': 'ğŸº',
      'íƒœêµ­': 'ğŸœ'
    };
    return icons[category] || 'ğŸ½ï¸';
  };

