import React, { useState, useEffect } from 'react';
import { useFavorites } from '../context/FavoriteContext';
import { supabase } from '../services/supabase';
import { restaurantData } from '../data/restaurantData';

const LocationBasedRecommendation = () => {
  const [userLocation, setUserLocation] = useState(null);
  const [nearbyRestaurants, setNearbyRestaurants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const { addToFavorites, removeFromFavorites, isInFavorites } = useFavorites();

  // Îëê ÏßÄÏ†ê Í∞ÑÏùò Í±∞Î¶¨ Í≥ÑÏÇ∞ (ÌïòÎ≤ÑÏÇ¨Ïù∏ Í≥µÏãù)
  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // ÏßÄÍµ¨Ïùò Î∞òÏßÄÎ¶Ñ (km)
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = 
      Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * 
      Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    const distance = R * c; // km
    return distance;
  };

  // SupabaseÏóêÏÑú Î†àÏä§ÌÜ†Îûë Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
  const fetchRestaurants = async () => {
    try {
      // SupabaseÏóêÏÑú Î†àÏä§ÌÜ†Îûë Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
      const { data, error } = await supabase
        .from('restaurants')
        .select('*');

      if (error) {
        console.warn('Supabase Ïó∞Í≤∞ Ïã§Ìå®, Í∏∞Î≥∏ Îç∞Ïù¥ÌÑ∞ ÏÇ¨Ïö©:', error);
        // Í∏∞Î≥∏ Îç∞Ïù¥ÌÑ∞ Î∞òÌôò
        return restaurantData.map(restaurant => ({
          id: restaurant.id,
          name: restaurant.name,
          cuisine: restaurant.category,
          rating: restaurant.rating,
          address: restaurant.address,
          description: restaurant.description,
          latitude: restaurant.position.lat,
          longitude: restaurant.position.lng,
          phone: restaurant.phone,
          parking: restaurant.parking,
          businessHours: restaurant.hours,
          totalRatings: Math.floor(Math.random() * 100) + 10 // ÏûÑÏãú Îç∞Ïù¥ÌÑ∞
        }));
      }

      // Supabase Îç∞Ïù¥ÌÑ∞Í∞Ä ÏûàÏúºÎ©¥ ÏÇ¨Ïö©, ÏóÜÏúºÎ©¥ Í∏∞Î≥∏ Îç∞Ïù¥ÌÑ∞ ÏÇ¨Ïö©
      if (data && data.length > 0) {
        console.log('Supabase Îç∞Ïù¥ÌÑ∞ ÏÇ¨Ïö©:', data.length, 'Í∞ú Î†àÏä§ÌÜ†Îûë');
        return data.map(restaurant => ({
          ...restaurant,
          totalRatings: Math.floor(Math.random() * 100) + 10 // ÏûÑÏãú Îç∞Ïù¥ÌÑ∞
        }));
      } else {
        console.log('Supabase Îç∞Ïù¥ÌÑ∞ ÏóÜÏùå, Í∏∞Î≥∏ Îç∞Ïù¥ÌÑ∞ ÏÇ¨Ïö©');
        return restaurantData.map(restaurant => ({
          id: restaurant.id,
          name: restaurant.name,
          cuisine: restaurant.category,
          rating: restaurant.rating,
          address: restaurant.address,
          description: restaurant.description,
          latitude: restaurant.position.lat,
          longitude: restaurant.position.lng,
          phone: restaurant.phone,
          parking: restaurant.parking,
          businessHours: restaurant.hours,
          totalRatings: Math.floor(Math.random() * 100) + 10 // ÏûÑÏãú Îç∞Ïù¥ÌÑ∞
        }));
      }
    } catch (error) {
      console.warn('API Ìò∏Ï∂ú Ïã§Ìå®, Í∏∞Î≥∏ Îç∞Ïù¥ÌÑ∞ ÏÇ¨Ïö©:', error);
      return restaurantData.map(restaurant => ({
        id: restaurant.id,
        name: restaurant.name,
        cuisine: restaurant.category,
        rating: restaurant.rating,
        address: restaurant.address,
        description: restaurant.description,
        latitude: restaurant.position.lat,
        longitude: restaurant.position.lng,
        phone: restaurant.phone,
        parking: restaurant.parking,
        businessHours: restaurant.hours,
        totalRatings: Math.floor(Math.random() * 100) + 10 // ÏûÑÏãú Îç∞Ïù¥ÌÑ∞
      }));
    }
  };

  // ÏÇ¨Ïö©Ïûê ÏúÑÏπò Í∞ÄÏ†∏Ïò§Í∏∞
  const getUserLocation = async () => {
    setLoading(true);
    setError(null);

    if (!navigator.geolocation) {
      setError('Ïù¥ Î∏åÎùºÏö∞Ï†ÄÏóêÏÑúÎäî ÏúÑÏπò Ï†ïÎ≥¥Î•º ÏßÄÏõêÌïòÏßÄ ÏïäÏäµÎãàÎã§.');
      setLoading(false);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        try {
          const { latitude, longitude } = position.coords;
          setUserLocation({ lat: latitude, lng: longitude });

          // SupabaseÏóêÏÑú Î†àÏä§ÌÜ†Îûë Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
          const restaurants = await fetchRestaurants();

          // Í∞ÄÍπåÏö¥ ÏãùÎãπ Ï∞æÍ∏∞ (ÏúÑÎèÑ/Í≤ΩÎèÑÍ∞Ä ÏûàÎäî Í≤ΩÏö∞Îßå)
          const restaurantsWithDistance = restaurants
            .filter(restaurant => restaurant.latitude && restaurant.longitude)
            .map(restaurant => {
              const distance = calculateDistance(
                latitude, 
                longitude, 
                restaurant.latitude, 
                restaurant.longitude
              );
              return {
                ...restaurant,
                distance: distance,
                position: { lat: restaurant.latitude, lng: restaurant.longitude },
                businessHours: restaurant.businessHours || "11:00 - 22:00",
              };
            });

          // ÏúÑÎèÑ/Í≤ΩÎèÑÍ∞Ä ÏóÜÎäî Í≤ΩÏö∞ Í∏∞Î≥∏ ÏúÑÏπò Îç∞Ïù¥ÌÑ∞ Ï∂îÍ∞Ä
          const restaurantsWithoutLocation = restaurants
            .filter(restaurant => !restaurant.latitude || !restaurant.longitude)
            .map(restaurant => ({
              ...restaurant,
              distance: Math.random() * 10 + 1, // ÏûÑÏãú Í±∞Î¶¨ (1-11km)
              position: restaurant.position || { lat: 37.5665, lng: 126.9780 }, // Í∏∞Ï°¥ position ÏÇ¨Ïö© ÎòêÎäî Í∏∞Î≥∏Í∞í
              businessHours: restaurant.businessHours || "11:00 - 22:00",
            }));

          const allRestaurants = [...restaurantsWithDistance, ...restaurantsWithoutLocation];

